import React, {Component} from 'react';
import cytoscape from 'cytoscape';

class CytoContainer extends Component{
    constructor(props){
        super(props);

    }

    renderCytoscape(){
        this.cy_data = {};
        this.cy = cytoscape(
        {
            container: document.getElementById('cy'),
            ready: function(){ window.cy = this; },
            elements: JSON.parse(this.cy_data),
            style:[
            {   selector: "node", 
                style: {
                    "content": "data(content)",
                    "text-opacity": 0.7,
                    "width" : "auto",
                    "height" : "auto",
                    "text-valign": "bottom",
                    "text-halign": "right",
                    "text-outline-color": "#eee",
                    "text-outline-width": 1
              }
            },
            {   selector: "[typeshape]", 
                style: {
                    "shape":"data(typeshape)"
                }
            },
            {   selector: "node[typeshape='diamond']", 
                style: {
                    'background-color': '#CC9900'
                }
            },
            {
                selector: "node[typeshape='diamond'][content='Conflict']",
                style: {
                    'background-color': 'red'
                }
            },
            {
                selector: "node[typeshape='diamond'][content='Support']",
                style: {
                    'background-color': 'green'
                }
            },
            {   selector: "edge", 
                style: {
                    "line-color": "#9dbaea",
                    "target-arrow-shape": "triangle",
                    "target-arrow-color": "#9dbaea",
                    "curve-style": "bezier"
                }
            },
            {   selector: ":selected", 
                style: {
                    "border-width":"1",
                    "border-color":"black",
                    "background-color": "#3399CC"
                }
            },
            {   selector: ".atom-label", 
                style:{
                    "text-wrap": "wrap",
                    "text-max-width": 160
                }
            },
            {   selector: ".scheme-label", 
                style:{
                    "text-wrap": "wrap",
                    "text-max-width": 160
                }
            }
            ],
            boxSelectionEnabled: false,
            autounselectify: false,
            selectionType: "single",
            minZoom: 0.1,
            maxZoom: 1.5

            })
    }//close rendercytoscape


    render(){
        return(
            <div id="cy"></div>
            );
    }
}

export default CytoContainer;
