import React, { Component } from 'react';
import ReactDOM from 'react-dom';

import cytoscape from 'cytoscape';
import edgehandles from 'cytoscape-edgehandles';
import contextMenus from 'cytoscape-context-menus';
import 'cytoscape-context-menus/cytoscape-context-menus.css';

import $ from 'jquery';


import {sd,add_scheme, add_edge, get_atom, now, new_uuid, update_atom_text, get_sd, delete_edge, update, add_source} from '../lib/Sadface'; 

import Modal from  './Modal';


cytoscape.use( edgehandles );
cytoscape.use(contextMenus, $);




var cy = null;
var cm = null;
var selected = [];
var position = null;
var layout = null;
var running = false;
var json;
var created_date = now();
var edited_date = now();
var analyst_email = "Default Analyst Email";
var analyst_name = "Default Analsyst Name";
var document_id = new_uuid();
var default_sadface_doc = "{\"analyst_email\":\""+analyst_email+"\",\"analyst_name\":\""+analyst_email+"\",\"created\":\""+created_date+"\",\"edges\":[],\"edited\":\""+edited_date+"\",\"id\":\""+document_id+"\",\"metadata\":{},\"nodes\":[],\"resources\": []}";
var current_sadface_doc = null;
var focused = null;
var undo_stack = [];
var redo_stack = [];
var edit_atom = null;
var resource_pane_viewable_state = true;



class Cytoscape extends Component {
  constructor(props){
        super(props);
        this.atomModalContent = React.createRef();

        this.state={
         cy_data: this.props.cy_data,
         png:'',
         jpg:'',
        }
         this.initCytoscape = this.initCytoscape.bind(this);
         this.build_cola_layout = this.build_cola_layout.bind(this);
         this.edit_atom_content = this.edit_atom_content.bind(this);
         this. delete_nodes =this.delete_nodes.bind(this);
         this.update_local_storage = this.update_local_storage.bind(this);
         this.redraw_visualisation = this.redraw_visualisation.bind(this);
         this.undo = this.undo.bind(this);
         this.redo = this.redo.bind(this);
         this.merge_nodes = this.merge_nodes.bind(this);
         this.delete_atom =this.delete_atom.bind(this);
        //    this.dragover_handler = this.dragover_handler.bind(this);
        this.handleModalClick = this.handleModalClick.bind(this);
    }

    initCytoscape(){
        console.log("initCyto");
        console.log(this.state.cy_data);
        cy = cytoscape({
            container: document.getElementById('cy'),
            elements:this.state.cy_data,

            style:[
                {   selector: "node", 
                    style: {
                        "label": "data(content)",
                        "text-opacity": 0.7,
                        "width" : "auto",
                        "height" : "auto",
                        "text-valign": "bottom",
                        "text-halign": "right",
                        "text-outline-color": "#eee",
                        "text-outline-width": 1
                    }
                },

                {   selector: "[typeshape]", 
                    style: {
                        "shape":"data(typeshape)"
                    }
                },

                {   selector: "node[typeshape='diamond']", 
                    style: {
                        'background-color': '#CC9900'
                    }
                },

                {   selector: "node[typeshape='diamond'][content='Conflict']",
                    style: {
                        'background-color': 'red'
                    }
                },

                {   selector: "node[typeshape='diamond'][name='Support']",
                    style: {
                        'background-color': 'green'
                    }
                },

                {   selector: "edge", 
                    style: {
                        "line-color": "#9dbaea",
                        "target-arrow-shape": "triangle",
                        "target-arrow-color": "#9dbaea",
                        "curve-style": "bezier"
                    }
                },


                {   selector: ":selected", 
                    style: {
                        "border-width":"1",
                        "border-color":"black",
                        "background-color": "#3399CC"
                    }
                },


                {   selector: ".atom-label", 
                    style:{
                        "text-wrap": "wrap",
                        "text-max-width": 160
                    }
                },

                {   selector: ".scheme-label", 
                    style:{
                        "text-wrap": "wrap",
                        "text-max-width": 160
                    }
                }
            ],

            boxSelectionEnabled: false,
            autounselectify: false,
            selectionType: "single",
            minZoom: 0.1,
            maxZoom: 1.5

        });//close cy = cytoscape()



    layout = this.build_cola_layout();
    layout.run();

    cy.edgehandles({
        toggleOffOnLeave: true,
        handleNodes: "node",
        handleSize: 10,
        handleColor: "orange",
        handleHitThreshold: 8,
        handleLineWidth: 5,
        //handleLineType: "flat",
        handleOutlineColor: "pink",
        edgeType: function(){ return "flat"; },
        complete: function(event, sourceNode, targetNode, addedEles){
            if (targetNode.length !== 0) {
                var source_id = targetNode[0].source().id();
                var target_id = targetNode[0].target().id();

                //get the mid point between source node and target node
                var source_position = targetNode[0].source().position();
                var target_position = targetNode[0].target().position();

                position = {};
                position.x = ((source_position.x + target_position.x)/2);
                position.y = ((source_position.y + target_position.y)/2);

                if (targetNode[0].source().data().type == "atom" && targetNode[0].target().data().type == "atom")
                {
                    var scheme = add_scheme("Support");
                    var scheme_id = scheme.id;
                    var scheme_content = scheme.name;
                    //remove the automatically generated edge
                    targetNode.remove();
                    cy.add([
                        {group: "nodes", data: {id: scheme_id.toString(),
                            content: scheme_content, typeshape: "diamond" }, classes: "scheme-label", locked: false, position: position}
                            ]);
                    var edge1 = add_edge(source_id, scheme_id);
                    var edge2 = add_edge(scheme_id, target_id);
                    cy.add([
                      { group: "edges", data: { id: edge1.id.toString(), source: source_id, target: scheme_id } },
                      { group: "edges", data: { id: edge2.id.toString(), source: scheme_id, target: target_id } }
                      ]);
                } else {
                    targetNode.remove();
                    var edge = add_edge(source_id, target_id);
                    cy.add([
                      { group: "edges", data: { id: edge.id.toString(), source: source_id, target: target_id } }
                      ]);
                }
                this.update_local_storage();
            } else {
                targetNode.remove();
            }
        }
    });
    /*
     *
     * Set up context menus
     *
     * */
