 build_cola_layout( opts ) {
    var cola_params = {
        name: "cola",
        animate: true,
        randomize: true,
        padding: 100,
        fit: false,
        maxSimulationTime: 1500
    };
    var i = 0;
    if (opts !== undefined) {
        opts.forEach(function(opt) {
         cola_params[i] = opts[i];
         ++i;
     });
    }
    return cy.makeLayout( cola_params );
}

