import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons'
import Modal from './Modal';
import Button from './MyButton';
import ResourcePaneTabContent from './resourcePaneTabContent'



class ResourcePane extends Component {
  constructor(props){
    super(props);

    this.state ={
      current_tab: 0,
      last_number: 1,
      tabs: [{tab_id:1},
                {tab_id:2},
                {tab_id:3},
                {tab_id:4}],
      tab_id:''
    };
    this.add_tab = this.add_tab.bind(this);
    this.add_resource = this.add_resource.bind(this);
    this.add_resource_header = this.add_resource_header.bind(this);
    this.next_number = this.next_number.bind(this);
    this.onClickMe = this.onClickMe.bind(this);

   //  this.set_active_tab = this.set_active_tab.bind(this);


  }

onClickMe() {
  console.log("onClickMe");
}




