import React, {Component} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';
//Import Components
import Modal from './Modal';
import TabBody from './TabBody';

import{add_resource, add_resource_metadata, get_sd} from '../lib/Sadface'; 






class ResourcePane extends Component {
    constructor(props) {
        super(props);
        this.state={
            resourceType: 'text',
            current_tab: null,
        }

        this.setType = this.setType.bind(this);
        this.addTab = this.addTab.bind(this);
        this.setActiveTab = this.setActiveTab.bind(this);


    };

    addTab(){
        let newTab = {}
        console.log('load tab null')
        newTab = add_resource(' ');
        console.log(newTab, 'newTab');
        add_resource_metadata(newTab.id, 'title', '');

        newTab.metadata = {title: ''}
        console.log(newTab, 'newTab with metadata')


        localStorage.setItem("state", JSON.stringify(get_sd()));
        newTab.type = this.state.resourceType;
        this.props.add_tab_to_tab_array(newTab);   
    }

    setActiveTab(value){
        console.log("setActiveTabs");
        this.setState({
            current_tab: value
        }, function (){
            console.log(this.state.current_tab, 'active');
        });
    }

     setType(e){
        this.setState({resourceType: e.target.value.toLowerCase()}, () =>
            console.log(this.state.resourceType, 'type set'));
    }

    change_title(tab_id, val) {
        this.set_text_resource_title(tab_id, val);
        this.updateTab(val, '');
    }



    // handleNewAtomText(){
    //      if(this.props.focused !== null || this.props.focused !== undefined){
    //         if(this.props.focused.parentNode.id === "textarea"){
    //             var elem = document.getElementById(this.props.focused.id);

    //             var selection = elem.value.substring(elem.selectionStart, elem.selectionEnd);
    //             console.log(selection, 'selection');
    //             console.log(elem, 'elem');
    //           //  if(selection != null || selection != undefined){
    //             //   add_new_atom_node(selection);
    //              //   elem.selectionStart = elem.selectionEnd;
    //              //   clear_focus();
    //            // }
    //         }
    //     }
    //     else { console.log("Not a valid text source") }

    // }
    componentDidMount(){
        //this.setActiveTab(this.props.tabs[this.props.tabs.length - 1].id);
        console.log(this.props.tabs, "COMPONET");
    }



      componentWillMount(){
        console.log(this.props.tabs,"WILLMOIUNT")
      }

      // componentWillReceiveProps(nextProps){
      //   console.log(this.props.tabs, "will recieve");
      //   if(this.props.tabs.length > 0){
      //       console.log("longer");
      //       let length = this.props.tabs.length - 1;
      //       this.setActiveTab(this.props.tabs[length].id);
      //   }
      // }



    componentDidUpdate(prevProps, prevSate) {

      if (prevProps.tabs.length !== this.props.tabs.length) {
        let length = this.props.tabs.length - 1;
           this.setActiveTab(this.props.tabs[length].id)
        console.log(this.props.tabs, 'UPDATE TABS');
        this.setState({tabs: this.props.tabs})
   ;
      }
     // if(this.props.selectedText !== prevProps.selectedText){
     //       this.setModalValue(this.props.selectedText);
     // }
    }





    render() {
        let num =0;






        const newResourceModal = {
            trigger: {
                triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
                className: 'add_tab_button',
                triggerId: "add_tab_button",
            },
            id:"resource_pane_selection_modal",
            title: 'New Resource Pane',
            content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type" onChange={this.setType}>
                <option>Text</option>
                </select>
                </div>,
            footer: {
                buttonNo: 2,
                button1: {
                    className: "btn btn-secondary",
                    value: "Close"
                },
                button2: {
                    className: "btn btn-primary",
                    value: "Add"
                }
            }
        };


        return ( 
            <div className="resource-pane" id="resource-pane" style={{display: this.props.showResourcePane ? "block" : "none"}}> 
                <div className="resource_pane_tab_head" id="resource_pane_tab_head">
                    <Modal
                        modalProps = {newResourceModal}  
                        onClick = {this.addTab} 

                    />
                    {
                        this.props.tabs.map((item, i)=> {
                            const onSetActiveTab = () =>
                                this.setActiveTab(item.id);
                                    return(
                                        <button  
                                            key={item.id + '_btn'}
                                            onClick={onSetActiveTab} 
                                            className={this.state.current_tab === item.id ? "tablinks active" : "tablinks"}
                                            id={item.id+"_btn"}
                                        >
                                            {++num}
                                        </button>
                                    );
                        })
                    }
                </div>

                        <div className="tab_body" id="tab_body">
                            {this.props.tabs.map((item,i) => {
                                if(item.type === "text"){
                                this.loadTxtRef = React.createRef();


                                const handleRemove = () =>
                                    this.props.remove_tab(i);

                                const clickLoad =() => {

                                    this.loadTxtRef.current.click();

                                }

                                const handleFileUpload = () =>{
                                var files = this.loadTxtRef.current.files;
                                this.props.fileManager('load','txt',  item.tab_id ,files);

                                }
                                const handleFileDownload = () =>{

                                    this.props.fileManager('save', 'txt', item.tab_id); 
                                }
                                const handleChange_title = (e) =>{
                                    var titleValue = e.target.value;
                                    this.props.change_title(item.tab_id, titleValue);
                                }

                                const handleChangeContent = (e) =>{
                                    var contentValue = e.target.value;
                                    this.props.change_content(item.tab_id, contentValue);
                                }
                                const handleAtomFromText = (e) =>{
                                    console.log("atom from text")
                                    console.log(this.props.selectedText, "atom from text selected");
                                    this.props.addAtomFromText();
                                }
                                return(
                                    <TabBody
                                        id={item.id}
                                        key={item.id + '_bdy'}
                                        i={i}
                                        style={{display: this.state.current_tab === item.id ? 'block' : 'none'}}

                                        handleremove={handleRemove}
                                        handleFileUpload={handleFileUpload}
                                        handleFileDownload={handleFileDownload}
                                        clickLoad={clickLoad}
                                        handleAtomFromText = {handleAtomFromText}
                                        focus={this.props.focus}
                                        clearSelection={this.props.clearSelection}


                                        titleValue={item.metadata.title}
                                        contentValue = {item.content}

                                        handleChange_title={handleChange_title}
                                        handleChangeContent={handleChangeContent}
                                        set_focus={this.props.set_focus}


                                        loadTxtRef={this.loadTxtRef}
                                        setSelectedText={this.props.setSelectedText}

                                    />
                                );
                            }
                            })}
                        </div>
            </div>
        );
    }
}















export default ResourcePane;
