        trigger: {
          triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
          className: 'add_tab_button',
          triggerId: "add_tab_button",
      },
      id:"resource_pane_selection_modal",
      title: 'New Resource Pane',
      content:  <div className="modal-body">
      <p>Select a resource type</p>
      <select className="form-control" id="resource_type">
      <option>Text</option>
      </select>
      </div>,
      footer: {
          className: 'btn btn-primary',
          value: 'Add',
     // action: this.add_tab() 
 }
};


