class TabBody extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            readOnly: false,
        }
        this.toggle_edit_lock = this.toggle_edit_lock.bind(this);

    }

    toggle_edit_lock(){
        this.setState({readOnly: !this.state.readOnly})

    }

    render() { 
        const {
          tabs,
          id,
          i,
          activeTab,
          handleremove,
          handleLoad,
          fileManager,
          style,
          loadTrigger,
          ref,
          clickLoad,
//element references
loadRef,
titleRef,
contentRef,

handleFileUpload,
handleChange_title,
handleChange_content



}=this.props



return (
    <div id={id+"_body"} className="resource_pane_tab_content" style={style}>
    <form>
    <div className="form-group">

    <Button 
    type="button" 
    className="btn btn-default" 
    title="Remove this tab from the resource pane"
    onClick={handleremove}
    variant="light"
    >
    <i className="fa fa-trash fa-fw fa-lg"></i>
    </Button>
    <input 
    type="file" 
    id={"load" + id} 
    style={{display: "block"}} 
    accept=".txt" 
    ref={loadRef}
    onChange= {handleFileUpload}
    />
    <Button 
    type="button" 
    id={"load" + id + "_btn"} 
    className="btn btn-default" 
    onClick={clickLoad}

    title="Load a text file into this resource tab"
    variant="light"
    >
    <i className="fa fa-upload fa-fw fa-lg"></i>

    </Button>
    <Button 
    type="button" 
    className="btn btn-default" 
    onClick={fileManager}
    /*onclick="filemanager('save','txt','` + tab_id + `')"*/ 
    title="Save this resource tab to a text file"
    variant="light"
    >
    <i className="fa fa-download fa-fw fa-lg"></i>
    </Button>
    <Button id="toggle_edit_lock_button" 
    type="button" 
    className="btn btn-default" 
    title="Toggle editability of the content area" 
    onClick={this.toggle_edit_lock}
    variant="light"
    >
    <i id={"toggle_edit_lock_icon_" + id} 
    className={(this.state.readOnly) ? "fa fa-lock fa-fw fa-lg" : "fa fa-unlock fa-fw fa-lg"}></i>
    </Button>
    <Button 
    type="button" 
    className="btn btn-default" 
    title="Add node from text selection"
    /* onclick="new_atom_txt_resource_button();"*/
    variant="light"
    >
    <i className="fa fa-puzzle-piece fa-fw fa-lg"></i>
    </Button>
    </div>
    <div className="form-group">
    <label>Title</label>
    <textarea 
    id={"title_"+id} 
    type="text" 
    rows="1" 
    style={{resize: "none"}} 
    className="form-control" 
    placeholder="The name of this resource..." 
    onChange={handleChange_title}
    ref={titleRef}
    >
    </textarea> 
    <label>Content</label>
    <div id="textarea">
    <textarea 
    id={id} 
    className="form-control" 
    placeholder="Enter your source text here..." 
    rows="20" 
    ref={contentRef}
    onChange={handleChange_content} 
    /*onfocus="set_focus(this)"*/ 
    style={{resize: "vertical", minHeight: "35px"}} 
    readOnly={this.state.readOnly}>
    </textarea>
    </div>  
    </div> 
    </form>
    </div>
    );
}


}   






export default ResourcePane;
