import React, {Component} from 'react';
import './CSS/monkeypuzzle.css';
//import './CSS/font-awesome.min.css';
import './CSS/cytoscape-context-menus.css';
//import './CSS/jquery.qtip.min.css';
//import './CSS/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './CSS/App.css';
import {Modal, Button} from 'react-bootstrap';


var FontAwesome = require('react-fontawesome');
const interfaceDivStyle = {
  marginLeft:'20px',
  marginRight:'60px'
}

const textAreaStyle = {
  resize: 'none'
}



class App extends Component{
  constructor(props){
    super(props);
      this.state = {
      }
    }


  render(){

    return (
      <div>
        <Interface />
        <MainUI />
        <Modal />

      </div>
      );
  }
}

class MainUI extends Component{
constructor(props){
    super(props);
      this.state = {
        addModalShow: false,
      }
    }

  render() {
        let addModalClose = () => this.setState({addModalShow:false});

    return (
      <main id="panel" className="panel">
        <div className="ui-container">
          <div className="resource-pane" id="resource-pane">
            <div className="resource_pane_tab_head" id="resource_pane_tab_head">
                <Button 
                  id='add_tab_button'
                  className="add_tab_button"
                  onClick={() => this.setState({addModalShow: true})} 
                   //  /*style="color: #cbc5c1;"*/>
                >
                  <i className="fa fa-plus-square fa-lg"></i>
                </Button>
                <AddDepModal
                  show={this.state.addModalShow}
                  onHide={addModalClose}
                  modaltitle="New Resource Pane"
                  id="resource_pane_selection_modal"
                >
                  <div className="modal-body">
                    <p>Select a resource type</p>
                    <select className="form-control" id="resource_type">
                      <option>Text</option>
                    </select>
                  </div>
                  <div class="modal-footer">
                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                <button type="button" className="btn btn-primary" data-dismiss="modal" onclick="add_tab();">Add</button>
            </div>
                </AddDepModal>
            </div>
          </div>
        </div>

    </main>
      );
  }
}

class AddDepModal extends Component {
  constructor(props){
    super(props);
  }

  render(){
    const {
      className='',
      modaltitle,
      modalbody,
      footer
    } = this.props;
    return(
      <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <div class="modal-header">
                <button type="button" class="close" data-dismiss="modal">&times;</button>
                <h4 class="modal-title">New Resource Pane</h4>
            </div>

        </Modal>
    );
  }
}



// change to stateless function component 
class MyButton extends Component {
  render(){
    const {
      onClick,
      className = '',
      children = '',
      title
    } = this.props;

    return (
       <button
        onClick={onClick}
        className={className}
        title={title}
        type="button"
      >
        {children}
      </button>
    );
  }
}





class Interface extends Component {
  constructor(props){
    super(props);
    this.state = {
    };

    this.toggleResourcePane = this.toggleResourcePane.bind(this);
    this.confirmReset = this.confirmReset.bind(this);
    this.loadDemoArgument = this.loadDemoArgument.bind(this);
    this.set_analyst_name = this.set_analyst_name.bind(this);
    this.set_analyst_email = this.set_analyst_email.bind(this);
    this.filemanger = this.filemanger.bind(this);
    this.loadSADFace = this.loadSADFace.bind(this);
  }

   toggleResourcePane(){
      console.log('toggleResourcePane');
  }

  confirmReset(){
    console.log("reset");
    //$('#confirm_reset_modal').modal('show');"
  }

  loadDemoArgument(){
