                    { data: { source: 'aphid', target: 'rose' } }
                ]
            },

        style:[
            {   selector: "node", 
                style: {
                    "content": "data(content)",
                    "text-opacity": 0.7,
                    "width" : "auto",
                    "height" : "auto",
                    "text-valign": "bottom",
                    "text-halign": "right",
                    "text-outline-color": "#eee",
