import React, {Component} from 'react';
import './App.css';

class App extends Component {
  render() {
    return (
      <div className="App">
        <h2> Welcome to the Road</h2>
          <div className="menu">
            <Card />
          </div>
      </div>
    );
  }
}

class Nav extends Component {
render() {
  const {
    className,
  } = this.props;
  return (
    <div className={className}>
      <h2>navigation</h2>
        <MenuSection />
    </div>
    );
  }
}

class MenuSection extends Component {
  render() {
    const {
      className
  } = this.props;
  return (
    <div> 
      <h3 class="menu-section-title">Interface</h3>
        <ul class="menu-section-list">            
           <li>Toggle Resource Pane</li>
            <li>Reset</li>
            <li>Load Demo</li>
            <li><textarea></textarea></li>
            <li><textarea></textarea></li>
      </ul>
      </div>
    );
  }
} 

 class Card extends Component {
  constructor(){
    super();

    this.state = {
      showMenu: false,
    }
    this.showMenu = this.showMenu.bind(this);
  }

  showMenu(event) {
    event.preventDefault();
    this.setState({
      showMenu: true,
    });
  }
  render() {
    return (
      <div>
        <button onClick={this.showMenu}>
          Show menu
        </button>

        {
          this.state.showMenu
          ? (
             <div className="menu">
                <button> Menu item 1 </button>
                <button> Menu item 2 </button>
                <button> Menu item 3 </button>
              </div>
            )
          : (
            null
            )
        }

      </div>
    );
  }
}

export default App;
