    if (atom_id) {
        var atom = get_atom(atom_id);
        if (atom !== null && atom !== undefined) {
            var size = Object.keys(sd.nodes).length;
            for (var i = 0; i < size; ++i) {
                if (sd.nodes[i].id === atom.id) {
                    delete sd.nodes[i];
//                    sd.nodes = remove_falsy(sd.nodes);
                        return;
                }
            }
        }
    }
}







