import React, { Component } from 'react';
import Modal from './Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons'
import SlideInTrigger from './SlideInTrigger';
import ResourcePane from './ResourcePane';
import CytoContainer from './CytoContainer';

import {import_json, export_cytoscape} from '../lib/Sadface'; 






class MainUI extends Component {
  constructor(props){
    super(props);
    this.state ={
      current_tab:'0',
      cy_data: {},
    }
  };


  newResourceModal = {
    trigger: {
      triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
      className: 'add_tab_button',
      triggerId: "add_tab_button",
    },
    id:"resource_pane_selection_modal",
    title: 'New Resource Pane',
    content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type">
                    <option>Text</option>
                </select>
              </div>,
    footer: {
      className: 'btn btn-primary',
      value: 'Add',
     // action: this.add_tab() 
    }

  };

  set_active_tab(tab_id){
  if(this.props.tabs.length > 0){
    console.log("set active tabs "+tab_id);
     this.setState({current_tab:  tab_id.substring(0, tab_id.indexOf("_body")) });
     var i, tabcontent, tablinks;
  }
}

loadDemoArgument(){
    console.log('load');
     var demo_sadface_doc = "{\"analyst_email\":\"40170722@live.napier.ac.uk\",\"analyst_name\":\"Nathan Mair\",\"created\":\"2018-02-23T02:27:36\",\"edges\":[{\"id\":\"a1s1\",\"source_id\":\"a1\",\"target_id\":\"s1\"},{\"id\":\"a2s1\",\"source_id\":\"a2\",\"target_id\":\"s1\"},{\"id\":\"a3s2\",\"source_id\":\"a3\",\"target_id\":\"s2\"},{\"id\":\"s2a5\",\"source_id\":\"s2\",\"target_id\":\"a5\"},{\"id\":\"s1a4\",\"source_id\":\"s1\",\"target_id\":\"a4\"},{\"id\":\"a4s2\",\"source_id\":\"a4\",\"target_id\":\"s2\"}],\"edited\":\"2018-02-23T02:27:36\",\"id\":\"94a975db-25ae-4d25-93cc-1c07c932e2f9\",\"metadata\":{},\"nodes\":[{\"id\":\"a1\",\"metadata\":{},\"sources\":[],\"text\":\"Every person is going to die\",\"type\":\"atom\"},{\"id\":\"a2\",\"metadata\":{},\"sources\":[],\"text\":\"You are a person\",\"type\":\"atom\"},{\"id\":\"a3\",\"metadata\":{\"test\":\"test\"},\"sources\":[],\"text\":\"If you are going to die then you should treasure every moment\",\"type\":\"atom\"},{\"id\":\"a4\",\"metadata\":{},\"sources\":[],\"text\":\"You are going to die\",\"type\":\"atom\"},{\"id\":\"a5\",\"metadata\":{},\"sources\":[],\"text\":\"You should treasure every moment\",\"type\":\"atom\"},{\"id\":\"s1\",\"name\":\"Support\",\"type\":\"scheme\"},{\"id\":\"s2\",\"name\":\"Support\",\"type\":\"scheme\"}],\"resources\": []}";

    localStorage.setItem("state",demo_sadface_doc);
    var cy_data = export_cytoscape(import_json(demo_sadface_doc));
   // initCytoscape();
   this.setState({cy_data: cy_data})
  }

  render() {
    const {
      toggleSlideIn,
      addTab,

      remove_tab,
      fileManager,
      showResourcePane

    } = this.props;


    return (
            /* Main UI: START */

    <main id="panel" className="panel">
      <div className="ui-container">
          <ResourcePane 
              addTab = {addTab}
              tabs={this.props.tabs}
              remove_tab={this.props.remove_tab}
              fileManager={this.props.fileManager}
              showResourcePane={this.props.showResourcePane}
              activeTab={this.props.activeTab}
              setActiveTab={this.props.setActiveTab}
              fileManager={this.props.fileManager}
              change_title={this.props.change_title}
              change_content={this.props.change_content}

         />
          <div className="splitter" id="splitter">
          </div>

          <div className="visualisation-pane">
              <div id="navbar">

                  <SlideInTrigger
                    className="btn-hamburger toggle-button"
                    onClick={toggleSlideIn}
                    showResourcePane={showResourcePane}
                    loadDemoArgument={this.loadDemoArgument}
                  />
              </div>
              <CytoContainer 
                new_uuid={this.props.new_uuid}
                loadTabs= {this.loadTabs}
              />

          </div>
      </div>
    </main>

   /* Main UI: END */
    );
  }
}










export default MainUI;
