}

setCurrentSadFaceDoc(value){
    this.setState({current_sadface_doc: value}, () =>
        console.log("current sad face doc updated" ));
}



addAtomFromText(){
    if(this.state.selectedText !== null){
        this.setState({newAtomFromText: !true}, () =>
            console.log(this.state.newAtomFromText, "set addAtomFromText"));
    }
}

//Menu function
handleSetPng(png){
    this.setState({png: png}, () =>
