remove_all_tabs(){
    this.setState({tabs: [], last_number:1}, () =>{
        console.log(this.state.tabs,'remove all tabs');
        });
}

toggleButton(){
    this.slideout.toggle();
}

fileManager(operation, filetype, tab, files) {
    console.log(tab.id);
    /*
    Write text from tab to file or does a SADFace save operation
    */

    if ("save" === operation)    {
        let filename = '';
        // if it's a txt file
        if ("txt" === filetype) {   

            if (tab.metadata.title !== '') {
                 filename = tab.metadata.title+".txt";
            } else {
                 filename = "tab "+tab.id+"_text.txt";
            }
            let text = tab.content;
            download(filename,text);
        }

        // if it's a JSON file
        if ("json" === filetype) {
           let filename = this.state.fileName;
           console.log(filename, 'filename');
            if(filename.length === 0){ filename = "default"}
               saveSADFace(filename, filetype);
        }
    }

    //if it's a load operation
    if ("load" === operation) {
        // if it's a txt file
        if ("txt" === filetype) {


            let file = files[0];
            let filename = file.name;
            let reader = new FileReader();      
            reader.onload = function(e) {
                var content = reader.result;
                update_resource(tab.id, content, filename);
                let newResource = {};
                newResource.tab = tab;
                newResource.filename = filename;
                newResource.content =content;
                this.setState({newResource:newResource}, () =>
                    console.log(this.state.newResource, "newResource"));

                //this.updateTab(filename, content);
            }.bind(this);
            reader.readAsText(file);
        }
        // if it's a JSON file
        if ("json" === filetype) {
            console.log('IMPORT JSON');
            let files = document.getElementById("loadJSON").files;
            let file = files[0];
            let reader = loadSADFace(file);
            if (reader !== null){
            reader.onload = function(e) {
                let result = reader.result;
                localStorage.setItem("state",result);
                this.loadJSON(result);
                //let json = JSON.parse(result);
                this.setState({remove_all_tabs: !this.state.remove_all_tabs}, () =>
                    console.log(this.state.remove_all_tabs));
            }.bind(this);
        }
        }
    }
}





loadJSON(json_value) {
    let importJSON = import_json(json_value);
    localStorage.setItem("state",JSON.stringify(get_sd()));
    this.setCurrentSadFaceDoc(JSON.stringify(get_sd()))
    this.setState({json: importJSON}, () => {
        console.log('json state set');
        this.setState({tabs: [], last_number:0, current_tab: ''}, () =>{
            console.log('empty tabs');
            this.loadTabs(this.state.json.resources);
        });
        this.setCyData(export_cytoscape(this.state.json));
    });      
}








loadTabs(resources){
    console.log(resources.length, 'load tabs resources');
    if(resources.length >= 1){
    this.setState(state => {
            const tabs = this.state.tabs.concat(resources);
                return {
                    tabs,
                }
            }, () => {
                    console.log('added to array');
//                  this.setActiveTab(this.state.tabs[this.state.tabs.length - 1].id);
                }
        );

   }
 }

