import React, {Component} from 'react';
import Modal from './Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';
import Button from './Button';


class ResourcePane extends Component {
    constructor(props) {
        super(props);
        this.state = {
            //tabs: this.props.tabs,
            activeTab: 0,
            showPanel: "none",
        }
        this.setActiveTab = this.setActiveTab.bind(this);
        this.handlefileManager =this.handlefileManager.bind(this);

    };
	newResourceModal = {
    trigger: {
      triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
      className: 'add_tab_button',
      triggerId: "add_tab_button",
    },
    id:"resource_pane_selection_modal",
    title: 'New Resource Pane',
    content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type">
                    <option>Text</option>
                </select>
              </div>,
    footer: {
      className: 'btn btn-primary',
      value: 'Add',
     // action: this.add_tab() 
    }
};

setActiveTab(index) {
        this.setState({
            activeTab: index
        });
    }




handlefileManager(){
    this.props.fileManager()
}

showPanel(){
    if (this.props.showResourcePane){
        this.setState({showPanel:"block"}) 
    }else{
        this.setState({showPanel:"none"})
    }
}




 	render() {
        const{
            addTab,
            tabs,

        } =this.props;

        return ( 
 			<div className="resource-pane" id="resource-pane" style={{display: this.state.showPanel}}>
                    <div className="resource_pane_tab_head" id="resource_pane_tab_head">
                        <Modal
                            modalProps = {this.newResourceModal}  
                            onClick = {this.props.addTab}               
                        />
                        {tabs.map((item, i)=> {
                            const onSetActiveTab = () =>
                                this.setActiveTab(i)
                            return(
                                <Button  
                                    onClick={onSetActiveTab} 
                                    className={this.state.activeTab === i ? "tablinks active" : "tablinks"}
                                    key={item.tab_number}
                                    id={item.tab_id+"_btn"}
                                >
                                    {item.tab_number}
                                </Button>

                                )})}
                    </div>



                    <div className="tab_body" id="tab_body">
                        {tabs.map((item,i) => {
                            const handleRemove = () =>
                                this.props.remove_tab(i);

                            const handlefileManager = () =>
                                this.props.fileManager('save', 'txt', item.tab_id); 
                            return(
                                <TabBody
                                    id={item.tab_id}
                                    key={item.tab_number}
                                    i={i}
                                    activeTab={this.state.activeTab}
                                    handleremove={handleRemove}
                                    fileManager={handlefileManager}
                                />

                                )
                        })}
                    </div>
                </div>
        );

    }
}




class TabBody extends React.Component {
    render() { 
        const {
    		tabs,
            id,
            i,
            activeTab,
            handleremove,
            fileManager



    	   }=this.props

            return (
				<div id={id+"_body"} className="resource_pane_tab_content" style={{display: activeTab === i ? 'block' : 'none'}}>
                <form>
                    <div className="form-group">

                            <button 
                                type="button" 
                                className="btn btn-default" 
                                title="Remove this tab from the resource pane"
                                onClick={handleremove}
                            >
                                <i className="fa fa-trash fa-fw fa-lg"></i>
                            </button>
                            <input type="file" id={"load" + id} style={{display: "block"}} accept=".txt" /*onchange="filemanager('load','txt','` + tab_id + `')"*//>
                            <button 
                                type="button" 
                                id={"load" + id + "_btn"} 
                                className="btn btn-default" 
                                /*onclick="$('#load` + tab_id + `').trigger('click');" */
                                title="Load a text file into this resource tab">
                                <i className="fa fa-upload fa-fw fa-lg"></i>
                            </button>
                            <button 
                                type="button" 
                                className="btn btn-default" 
                                onClick={fileManager}
                                /*onclick="filemanager('save','txt','` + tab_id + `')"*/ 
                                title="Save this resource tab to a text file">
                                <i className="fa fa-download fa-fw fa-lg"></i>
                            </button>
                            <button id="toggle_edit_lock_button" type="button" className="btn btn-default" title="Toggle editability of the content area" /*onclick="toggle_edit_lock();"*/>
                                <i id={"toggle_edit_lock_icon_" + id} className="fa fa-lock fa-fw fa-lg"></i>
                            </button>
                            <button type="button" className="btn btn-default" title="Add node from text selection"/* onclick="new_atom_txt_resource_button();"*/>
                                <i className="fa fa-puzzle-piece fa-fw fa-lg"></i>
                            </button>
                    </div>
                    <div className="form-group">
                        <label>Title</label>
                        <textarea id={"title_"+id} type="text" rows="1" style={{resize: "none"}} className="form-control" placeholder="The name of this resource..." /*onchange="change_title('` + tab_id + `')"*/></textarea> 
                        <label>Content</label>
                        <div id="textarea">
                            <textarea id={id} className="form-control" placeholder="Enter your source text here..." rows="20" /*onchange="change_textarea('` + tab_id + `')" onfocus="set_focus(this)"*/ style={{resize: "vertical", minHeight: "35px"}} readOnly></textarea>
                        </div>  
                    </div> 
                </form>
              </div>
            );
    }


}   






export default ResourcePane;
