import React, {Component} from 'react';

const textAreaStyle = {
  resize: 'none'
}

class TextArea extends Component {
  render(){
    const {
      className = '',
      title = '',
      placeholder,
      id,
      rows,
      cols,
      maxLength,
      onChange,
      children,
      style

    } = this.props;

    return (
      <textarea
      className={className}
      title={title}
      placeholder={placeholder}
      id={id}
      rows={rows}
      cols={cols}
      maxLength = {maxLength}
      onChange = {onChange}
      type="textarea"
      style={style}
      >
        {children}
      </textarea>
      );
  }
}

export default TextArea;
