import React, { Component } from 'react';
import Modal from './Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons'
import SlideInTrigger from './SlideInTrigger';



class MainUI extends Component {
  newResourceModal = {
    trigger: {
      triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
      className: 'add_tab_button',
      triggerId: "add_tab_button",
    },
    id:"resource_pane_selection_modal",
    title: 'New Resource Pane',
    content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type">
                    <option>Text</option>
                </select>
              </div>,
    footer: {
      className: 'btn btn-primary',
      value: 'Add',
     // action: this.add_tab() 
    }

  };


  render() {
    const {
      toggleSlideIn,
      addTab,
      tabs

    } = this.props;
    return (
            /* Main UI: START */

    <main id="panel" className="panel">
    <div className="ui-container">
        <div className="resource-pane" id="resource-pane">
            <div className="resource_pane_tab_head" id="resource_pane_tab_head">
              <Modal 
                modalProps = {this.newResourceModal}
                onClick = {addTab}
                />
                <div>
              {this.props.tabs.map(item =>{
                const onHandleSetActiveTab = () =>
                this.set_active_tab(item.tab_id+'_body');

                  return (
                    <button
                      key={item.tab_number}
                      id={item.tab_id+'_btn'}
                      onClick = {onHandleSetActiveTab}
                      type="button"
                      className="tablinks"
                    >
                      {item.tab_number}
                    </button>
                  );
                }                                                     
                  )}

              </div>
            </div>
            <div className="tab_body" id="tab_body"></div>        
        </div>

        <div className="splitter" id="splitter">
        </div>

        <div className="visualisation-pane">
            <div id="navbar">

                <SlideInTrigger
                  className="btn-hamburger toggle-button"
                  onClick={toggleSlideIn}
                />
            </div>
            <div id="cy" onDrop="drop_handler(event);" onDragover="dragover_handler(event);"></div>
        </div>
    </div>
    </main>

   /* Main UI: END */
    );
  }
}



export default MainUI;
