fileManager(operation, filetype, tab_id) {
    /*
    Write text from tab to file or does a SADFace save operation
    */

    console.log(operation, filetype, tab_id);
    if ("save" == operation)    {
        let tab_to_download = this.getTab(this.state.current_tab);
        console.log(tab_to_download);
        // if it's a txt file
        if ("txt" == filetype) {   

            if (tab_to_download.metadata.title !== '') {
                var filename = tab_to_download.metadata.title+".txt";
            } else {
                var filename = "tab"+this.state.current_tab+"_text.txt";
            }
            var text = tab_to_download.content;
            this.download(filename,text);
        }

        // if it's a JSON file
        if ("json" == filetype) {
           var filename = this.state.fileName;
           console.log(filename, 'filename');
            if(filename.length == 0){ filename = "default"}
               saveSADFace(filename, filetype);
        }
    }

    //if it's a load operation
    if ("load" == operation) {
        // if it's a txt file
        if ("txt" == filetype) {


            var files = document.getElementById("load"+this.state.current_tab).files;
            var file = files[0];
            var filename = file.name;
            var reader = new FileReader();      
            reader.onload = function(e) {
                var content = reader.result;
              //  document.getElementById(this.state.current_tab).innerText = content;
                update_resource(tab_id, content, filename);
                this.updateTab(filename, content);
            }.bind(this);
            reader.readAsText(file);
        }
        // if it's a JSON file
        if ("json" == filetype) {
            var files = document.getElementById("loadJSON").files;
            var file = files[0];
            var reader = loadSADFace(file);
            reader.onload = function(e) {
                var result = reader.result;
                localStorage.setItem("state",result);
                this.loadJSON(result);
                var json = JSON.parse(result);
                this.setState({remove_all_tabs: !this.state.remove_all_tabs}, () =>
                    console.log(this.state.remove_all_tabs));
            }.bind(this);
        }
    }
}

