import React, {Component} from 'react';
import './CSS/monkeypuzzle.css';
//import './CSS/font-awesome.min.css';
import './CSS/cytoscape-context-menus.css';
import './CSS/jquery.qtip.min.css';
//import './CSS/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const interfaceDivStyle = {
  marginLeft:'20px',
  marginRight:'60px'
}

const textAreaStyle = {
  resize: 'none'
}
class App extends Component {
  constructor(props){
    super(props);
    this.state = {
    };

    this.toggleResourcePane = this.toggleResourcePane.bind(this);
    this.confirmReset = this.confirmReset.bind(this);
    this.loadDemoArgument = this.loadDemoArgument.bind(this);
  }

   toggleResourcePane(){
      console.log('toggleResourcePane');
  }

  confirmReset(){
    console.log("reset");
    //$('#confirm_reset_modal').modal('show');"
  }

  loadDemoArgument(){
    console.log('load');
  }

  set_analyst_name(){
    console.log('set name');
  }

  set_analyst_email(){
    console.log('set email');
  }

  render() {
    return (

           <nav id="menu" className="menu">
          <a href="http://arg.napier.ac.uk/monkeypuzzle/">
                <header className="menu-header">
                  <span className="menu-header-title">MonkeyPuzzle</span>
                </header>
            </a>

            <section className="menu-section">
                <h3 className="menu-section-title">Interface</h3>
                <ul className="menu-section-list">            
                   <li>
                    <a href="#" id="toggle_resource">
                      <Button 
                        className="btn btn-default"
                        onClick={() => this.toggleResourcePane()}
                        title="Toggle the visibility of the resources pane"
                      >
                        Toggle Resource Pane
                      </Button>
                      </a>
                   </li>
                   <li>
                    <a href="#" id="clear_storage_button">
                      <Button
                        className="btn btn-danger"
                        onClick={() => this.confirmReset()}
                        title="Reset MonkeyPuzzle to her initial state"
                      >
                        Reset
                      </Button>
                    </a>
                   </li>
                   <li>
                     <a href="#" id="load_demo_argument_button">
                      <Button
                        className="btn btn-default"
                        onClick={() => this.loadDemoArgument()}
                        title="Load a demonstration SADFace document into MonkeyPuzzle"
                      >
                        Load Demo 
                      </Button>
                     </a>
                   </li>
                   <li>
                     <div style={interfaceDivStyle}>
                        <TextArea 
                          className = "form-control"
                          id= "analyst_name_textarea"
                          rows="1"
                          cols="20"
                          maxLength="40"
                          placeholder="Name"
                          title="Type your name here..."
                          onChange={() => this.set_analyst_name()}

                        />
                     </div>
                   </li>
                    <li>
                     <div style={interfaceDivStyle}>
                        <TextArea 
                          className = "form-control"
                          id= "analyst_email_textarea"
                          rows="1"
                          cols="20"
                          maxLength="40"
                          placeholder="Email"
                          title="Type your email here..."
                          onChange={() => this.set_analyst_email()}
                          style={textAreaStyle}
                        />
                     </div>
                   </li>
                </ul>
              </section>

              <section className="menu-section">
                <h3 className="menu-section-title">Export</h3>
                <ul className="menu-section-list">
                  <li>
                    <div style={interfaceDivStyle}>
                      <TextArea
                        className="form-control"
                        id="export_filename"
                        rows="1"
                        cols="20"
                        maxLength="40"
                        placeholder="Filename"
                        title="The name for the exported file"
                      />
                    </div>
                  </li>
                  <li>
                    <a href="#" id="export_button">
                      <Button
                        className="btn btn-default"
                        onClick={() => this.download_sadface()}
                        title="Export the current analysis to a SADFace formatted JSON document"
                      >
                        SADFace
                      </Button>
                    </a>
                  </li>
                  <li>
                    <a href="#" /*target="_blank"*/ id="download_png">
                      <Button 
                        className="btn btn_default"
                        title="Export the current analysis to a PNG image"
                        onClick={() => this.download_png()}
                      >
                        PNG
                      </Button>
                    </a>
                  </li>
                  <li>
                    <a href="#" /*target="_blank"*/ id="download_jpg">
                      <Button
                        className="btn btn_default"
                        title="Export the current analysis to a JPG image"
                        onClick={() => this.download_jpg()}
                      >
                        JPG
                      </Button>
                    </a>
                  </li>

                </ul>

              </section>

              </nav>
    );
  }
}
// change to stateless function component 
class Button extends Component {
  render(){
    const {
      onClick,
      className = '',
      children = '',
      title
    } = this.props;

    return (
       <button
        onClick={onClick}
        className={className}
        title={title}
        type="button"
      >
        {children}
      </button>
    );
  }
}

class TextArea extends Component {
  render(){
    const {
      className = '',
      title = '',
      placeholder,
      id,
      rows,
      cols,
      maxLength,
      onChange,
      children,
      style

    } = this.props;

    return (
      <textarea
      className={className}
      title={title}
      placeholder={placeholder}
      id={id}
      rows={rows}
      cols={cols}
      maxLength = {maxLength}
      onChange = {onChange}
      type="textarea"
      style={style}
      >
        {children}
      </textarea>
      );
  }
}


export default App;
