cytoscape.use( edgehandles );
cytoscape.use( cola );





class GraphContainer extends React.Component{
    constructor(props){
        super(props);
        this.cy=null;
        this.layout = null;
        this.position=null;

        this.state = {
        	cy:null,
            cm: null,
            selected: [],
            position: [],
            layout: null,
            running: false,
            cy_data: {
                nodes: [
                    { data: { id: 'cat' } },
                    { data: { id: 'bird' } },
                    { data: { id: 'ladybug' } },
                    { data: { id: 'aphid' } },
                    { data: { id: 'rose' } },
                    { data: { id: 'grasshopper' } },
                    { data: { id: 'plant' } },
                    { data: { id: 'wheat' } }
                ],
                edges: [
                    { data: { source: 'cat', target: 'bird' } },
                    { data: { source: 'bird', target: 'ladybug' } },
                    { data: { source: 'bird', target: 'grasshopper' } },
                    { data: { source: 'grasshopper', target: 'plant' } },
                    { data: { source: 'grasshopper', target: 'wheat' } },
                    { data: { source: 'ladybug', target: 'aphid' } },
                    { data: { source: 'aphid', target: 'rose' } }
                ]
            },
            json: '',
            created_date: '',
            edited_date:'',
            analyst_email: "Default Analyst Email",
            analyst_name:'',
            document_id: '',
            default_sadface_doc: {},
            current_sadface_doc: null,
            focused: null,
            undo_stack: [],
            redo_stack: [],
            edit_atom: null,
            resource_pane_viewable_state: true,

            sd:{},





        }
        this.initialise_monkeypuzzle = this.initialise_monkeypuzzle.bind(this);
    }




    initialise_monkeypuzzle(){
          //load diagram if there is one in localStorage
        if (localStorage.getItem("state"))
        {
            this.loadJSON(localStorage.getItem("state"));
            this.initCytoscape();
        //else use default
        } else
        {
            this.setState({current_sadface_doc: this.setDefault()})
            localStorage.setItem("state", this.state.current_sadface_doc);
         //   cy_data = export_cytoscape({import_json(current_sadface_doc)});
            this.initCytoscape();
        }
    }


    initCytoscape(){
        this.cy = cytoscape({
        container: document.getElementById("cy"),
        ready: function(){ window.cy = this; },
        elements: this.state.cy_data,
        style:[
            {   selector: "node", 
                style: {
                    "content": "data(content)",
                    "text-opacity": 0.7,
                    "width" : "auto",
                    "height" : "auto",
                    "text-valign": "bottom",
                    "text-halign": "right",
                    "text-outline-color": "#eee",
                    "text-outline-width": 1
              }
            },
            {   selector: "[typeshape]", 
                style: {
                    "shape":"data(typeshape)"
                }
            },
            {   selector: "node[typeshape='diamond']", 
                style: {
                    'background-color': '#CC9900'
                }
            },
            {
                selector: "node[typeshape='diamond'][content='Conflict']",
                style: {
                    'background-color': 'red'
                }
            },
            {
                selector: "node[typeshape='diamond'][content='Support']",
                style: {
                    'background-color': 'green'
                }
            },
            {   selector: "edge", 
                style: {
                    "line-color": "#9dbaea",
                    "target-arrow-shape": "triangle",
                    "target-arrow-color": "#9dbaea",
                    "curve-style": "bezier"
                }
            },
            {   selector: ":selected", 
                style: {
                    "border-width":"1",
                    "border-color":"black",
                    "background-color": "#3399CC"
                }
            },
            {   selector: ".atom-label", 
                style:{
                    "text-wrap": "wrap",
                    "text-max-width": 160
                }
            },
            {   selector: ".scheme-label", 
                style:{
                    "text-wrap": "wrap",
                    "text-max-width": 160
                }
            }
            ],
            boxSelectionEnabled: false,
            autounselectify: false,
            selectionType: "single",
            minZoom: 0.1,
            maxZoom: 1.5
    });

    this.layout = this.build_cola_layout();
    this.layout.run();
/*
    this.cy.elements("node[type = \"atom\"]").qtip({
        content: function(){return 'ID: '+this.id()},
        position: {
            my: "top center",
            at: "bottom center"
        },
        style: {
            classes: "qtip-bootstrap",
            tip: {
                width: 16,
                height: 8
            }
        }
    });
*/

   this.cy.edgehandles({
        toggleOffOnLeave: true,
        handleNodes: "node",
        handleSize: 10,
        handleColor: "orange",
        handleHitThreshold: 8,
        handleLineWidth: 5,
        //handleLineType: "flat",
        handleOutlineColor: "grey",
        edgeType: function(){ return "flat"; },
        complete: function(event, sourceNode, targetNode, addedEles){
            if (targetNode.length !== 0) {
                var source_id = targetNode[0].source().id();
                var target_id = targetNode[0].target().id();

                //get the mid point between source node and target node
                var source_position = targetNode[0].source().position();
                var target_position = targetNode[0].target().position();

                this.position = {};
                this.position.x = ((source_position.x + target_position.x)/2);
                this.position.y = ((source_position.y + target_position.y)/2);

                if (targetNode[0].source().data().type == "atom" && targetNode[0].target().data().type == "atom")
                {
                    var scheme = this.add_scheme("Support");
                    var scheme_id = scheme.id;
                    var scheme_content = scheme.name;
                    //remove the automatically generated edge
                    targetNode.remove();
                    this.cy.add([
                        {group: "nodes", data: {id: scheme_id.toString(),
                            content: scheme_content, typeshape: "diamond" }, classes: "scheme-label", locked: false, position: this.position}
                    ]);
                    var edge1 = add_edge(source_id, scheme_id);
                    var edge2 = add_edge(scheme_id, target_id);
                    this.cy.add([
                      { group: "edges", data: { id: edge1.id.toString(), source: source_id, target: scheme_id } },
                      { group: "edges", data: { id: edge2.id.toString(), source: scheme_id, target: target_id } }
                    ]);
                } else {
                    targetNode.remove();
                    var edge = add_edge(source_id, target_id);
                    this.cy.add([
                      { group: "edges", data: { id: edge.id.toString(), source: source_id, target: target_id } }
                    ]);
                }
                this.update_local_storage();
            } else {
                targetNode.remove();
            }
        }
    });






    }//close init cytoscape
