    update_local_storage() {
    var undo_item = JSON.parse(this.state.current_sadface_doc);
    this.state.undo_stack.push(undo_item);
    this.state.redo_stack = [];
    this.state.cm.showMenuItem("undo");
    this.state.cm.hideMenuItem("redo");
    localStorage.setItem("state", JSON.stringify(this.state.sd));
    this.setState({current_sadface_doc: JSON.stringify(this.state.sd)});
    update();
}
