setDefault(){
    this.setState({
        analyst_email: "Default Analyst Email",
        analyst_name: "Default Analsyst Name",
        created: this.props.now(),
        edges:[],
        edited: this.props.now(),
        id: this.props.new_uuid(),
        metadata:{},
        nodes:[],
        resources: [],



    });
}

loadJSON(json_value) {
  /*  json = import_json(json_value);
    localStorage.setItem("state",JSON.stringify(this.state.sd));
    current_sadface_doc = JSON.stringify(this.state.sd);
    //load any sources in the stored diagram state
    window.onload = function () {
        this.props.loadTabs(json.resources);
    };
    cy_data = export_cytoscape(json);
    if(cy !== null)
    {
        cy.elements().remove();
        cy.json({elements: JSON.parse(cy_data)});
        this.redraw_visualisation();
    }*/
}

redraw_visualisation() {
  //  this.state.layout.stop();
  //  layout.options.eles = cy.elements();
   // layout.run();
   // cy.center();
  //  cy.resize();
}

 build_cola_layout( opts ) {
    var cola_params = {
        name: "cola",
        animate: true,
        randomize: true,
        padding: 100,
        fit: false,
        maxSimulationTime: 1500
    };
    var i = 0;
    if (opts !== undefined) {
        opts.forEach(function(opt) {
           cola_params[i] = opts[i];
           ++i;
        });
    }
    return this.cy.makeLayout( cola_params );
}





    componentDidMount(){
        this.initialise_monkeypuzzle();
        this.setState({
            cy: this.cy,
            layout: this.layout,
            position: this.position,
        })

    }

    render(){
        return(
            <div id="cy" ondrop="drop_handler(event);" ondragover="dragover_handler(event);"></div>
        )
    }
}




export default GraphContainer;
