  constructor(props){
    super(props);
    this.state ={
      current_tab:'0',
    }
  };

  newResourceModal = {
    trigger: {
      triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
      className: 'add_tab_button',
      triggerId: "add_tab_button",
    },
    id:"resource_pane_selection_modal",
    title: 'New Resource Pane',
    content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type">
                    <option>Text</option>
                </select>
              </div>,
    footer: {
      className: 'btn btn-primary',
      value: 'Add',
     // action: this.add_tab() 
    }

  };

  set_active_tab(tab_id){
  if(this.props.tabs.length > 0){
    console.log("set active tabs "+tab_id);
     this.setState({current_tab:  tab_id.substring(0, tab_id.indexOf("_body")) });
     var i, tabcontent, tablinks;
