        for (i = 0; i < tabcontent.length; i++) {
            tabcontent[i].style.display = "none";
       }
      tablinks = document.getElementsByClassName("tablinks");
        for (i = 0; i < tablinks.length; i++) {
           tablinks[i].className = tablinks[i].className.replace("active", "");
      }
    if (document.getElementById(tab_id)) {
        document.getElementById(tab_id).style.display = "block";
        document.getElementById(this.state.current_tab+"_btn").className += " active";
    }*/

  }
}


  render() {
    const {
      toggleSlideIn,
      addTab,
    //  tabs

    } = this.props;

    return (
            /* Main UI: START */

    <main id="panel" className="panel">
    <h1>hello {this.state.current_tab}</h1>
    <div className="ui-container">
        <div className="resource-pane" id="resource-pane">
            <div className="resource_pane_tab_head" id="resource_pane_tab_head">
              <Modal 
                modalProps = {this.newResourceModal}
                onClick = {addTab}
                />
                <div> 
                <Tabs content={_tabs}/>              
            {/*}  {tabs.map(item =>{
                const onHandleSetActiveTab = () =>
                this.set_active_tab(item.tab_id+'_body');

                  return (
                    <div>
                    </div>

                  );
                }  */}                                                   
                  )}

              </div>
