class TabsNav extends Component {
    handleClick(index) {
        this.props.handleClick(index);
    }

    render() {
        let activeStyles = {
            backgroundColor: 'deepskyblue',
            borderColor: 'deepskyblue',
            color: '#fff'
        }

        let buttons = this.props.links.map((item, i) => {
            return (<button className="TabsNav-btn" onClick={this.handleClick.bind(this, i)} key={i} style={this.props.active === i ? activeStyles : {}}>{item.name}</button>);
        });

        return (
            <nav className="TabsNav">
                {buttons}
            </nav>
        );       
    }
}

