class Tabs extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: this.props.content,
            activeTab: 0
        }
    }

    handleClick(index) {
        this.setState({
            activeTab: index
        });
    }

    render() {
        return (
            <div className="Tabs">
                <TabsNav 
                  links={this.state.items} 
                  handleClick={this.handleClick.bind(this)} 
                  active={this.state.activeTab} 
                />
                <TabsContent 
                  content={this.state.items} 
                  active={this.state.activeTab} 
                  />
            </div>
        );
    }
}







