import React, {Component} from 'react';
import './CSS/monkeypuzzle.css';
import './CSS/App.css';
import './CSS/App.css';
import './CSS/App.css';
import './CSS/App.css';
import './CSS/App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
    };

    this.toggleResourcePane = this.toggleResourcePane.bind(this);
    this.confirmReset = this.confirmReset.bind(this);
    this.loadDemoArgument = this.loadDemoArgument.bind(this);
  }

   toggleResourcePane(){
      console.log('toggleResourcePane');
  }

  confirmReset(){
    console.log("reset");
  }

  loadDemoArgument(){
    console.log('load');
  }

  set_analyst_name(){
    console.log('set name');
  }

  set_analyst_email(){
    console.log('set email');
  }

  render() {
    return (

           <nav id="menu" className="menu">
          <a href="http://arg.napier.ac.uk/monkeypuzzle/">
                <header className="menu-header">
                  <span className="menu-header-title">MonkeyPuzzle</span>
                </header>
            </a>

            <section className="menu-section">
                <h3 className="menu-section-title">Interface</h3>
                <ul className="menu-section-list">            
                   <li>
                    <a href="#" id="toggle_resource">
                      <Button 
                        className="btn btn-default"
                        onClick={() => this.toggleResourcePane()}
                        title="Toggle the visibility of the resources pane"
                      >
                        Toggle Resource Pane
                      </Button>
                      </a>
                   </li>
                   <li>
                    <a href="#" id="clear_storage_button">
                      <Button
                        className="btn btn-danger"
                        onClick={() => this.confirmReset()}
                        title="Reset MonkeyPuzzle to her initial state"
                      >
                        Reset
                      </Button>
                    </a>
                   </li>
                   <li>
                     <a href="#" id="load_demo_argument_button">
                      <Button
                        className="btn btn-default"
                        onClick={() => this.loadDemoArgument()}
                        title="Load a demonstration SADFace document into MonkeyPuzzle"
                      >
                        Load Demo 
                      </Button>
                     </a>
                   </li>
                   <li>
                     <div>
                        <TextArea 
                          className = "form-control"
                          id= "analyst_name_textarea"
                          rows="1"
                          cols="20"
                          maxlength="40"
                          placeholder="Name"
                          title="Type your name here..."
                          onChange={() => this.set_analyst_name()}
                        />
                     </div>
                   </li>
                    <li>
                     <div>
                        <TextArea 
                          className = "form-control"
                          id= "analyst_email_textarea"
                          rows="1"
                          cols="20"
                          maxlength="40"
                          placeholder="Email"
                          title="Type your email here..."
                          onChange={() => this.set_analyst_email()}
                        />
                     </div>
                   </li>
                </ul>
              </section>

              </nav>
    );
  }
}
// change to stateless function component 
class Button extends Component {
  render(){
    const {
      onClick,
      className = '',
      children = '',
      title
    } = this.props;

    return (
       <button
        onClick={onClick}
        className={className}
        title={title}
        type="button"
      >
        {children}
      </button>
    );
  }
}

class TextArea extends Component {
  render(){
    const {
      className = '',
      title = '',
      placeholder,
      id,
      rows,
      cols,
      maxLength,
      onChange,
      children

    } = this.props;

    return (
      <textarea
      className={className}
      title={title}
      placeholder={placeholder}
      id={id}
      rows={rows}
      cols={cols}
      maxLength = {maxLength}
      onChange = {onChange}
      type="textarea"
      >
        {children}
      </textarea>
      );
  }
}


export default App;
