                document.getElementById(tab_id).innerText = content;
                this.update_resource(tab_id, content, filename);
            }
            reader.readAsText(file);
        }
        // if it's a JSON file
        if ("json" == filetype) {
            var files = document.getElementById("loadJSON").files;
            var file = files[0];
            var reader = this.loadSADFace(file);
            reader.onload = function(e) {
                var result = reader.result;
                localStorage.setItem("state",result);
                this.loadJSON(result);
                var json = JSON.parse(result);
                this.remove_all_tabs();
                this.loadTabs(json.resources);
            }
        }
    }
}
 update_resource(resource_id, content, title) {
    /*
    Given an ID for an existing resource, update the content and metadata of the resource.
    Updates the resource in sd;
    */

    if (resource_id && (content || title)) {
        var resource = this.get_resource(resource_id);
        if (resource !== null && resource !== undefined) {
            if (content !== null && content !== undefined) {
                resource.content = content;
            }
            if (title !== null && title !== undefined) {
                resource.metadata.title = title;
            }
            this.setState.sd.resources.forEach((res) => {
               if(res.id === resource_id) {
                   res = resource;
                }
            }
            );
        } else {
            throw ("Could not update the resource: "+resource_id);
        }
    }
}


