import React, {Component} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons'
import Modal from './Modal';
import Button from './MyButton';
import ResourcePaneTabContent from './resourcePaneTabContent'



class ResourcePane extends Component {
  constructor(props){
    super(props);

    this.state ={
      current_tab: 0,
      last_number: 1,
      tabs: []
    };
    this.add_tab = this.add_tab.bind(this);
    this.add_resource = this.add_resource.bind(this);
    this.add_resource_header = this.add_resource_header.bind(this);
    this.next_number = this.next_number.bind(this);


    this.onClickMe = this.onClickMe.bind(this);


  }


  onClickMe() {
    console.log('<div>hello</div>');
    document.getElementById("resource_pane_tab_head").append('<button id="`+ tab_id + `_btn" className="tablinks"</button>')
  }

   next_number(){ return this.state.last_number++; }


   add_tab(load_id=null) {
  if (load_id == null) {
        var new_resource = this.add_resource(' ');
      tab_id = new_resource.id;
    //  add_resource_metadata(tab_id, 'title', '');
      //localStorage.setItem("state",JSON.stringify(get_sd()));
  } 
    else { tab_id = load_id; }

    add_resource_header();

    var resource_type_idx = document.getElementById("resource_type").options.selectedIndex;
    var resource_type_txt = document.getElementById("resource_type").options[resource_type_idx].text;
    if (resource_type_txt.toLowerCase() === "text") { add_text_resource_body(tab_id); }

    set_active_tab(tab_id+"_body");
    return tab_id
}

add_resource(content) {
    /*
    Create a new resource dict using the supplied content string
    then add to the resourses list of the sadface doc

    Returns: the new resource dict
    */
    if (content) {
        var res = new_resource(content);
        sd.resources.push(res);
        return res;
    }
}

 add_resource_header() {
    tab_number = next_number();
    tabs.push(tab_id);
    var resource_pane_tab_head = $(`
        <button id="`+ tab_id + `_btn" class="tablinks" 
            onclick="set_active_tab('` + tab_id + `_body')">` + 
            tab_number + `</button>
    `);

    $(".resource_pane_tab_head").append(resource_pane_tab_head);
}


      /* New Resource Pane Type Selection Modal */

  newResourceModal = {
    trigger: {
      triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
      className: 'add_tab_button',
    },

    title: 'New Resource Pane',
    content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type">
                    <option>Text</option>
                </select>
              </div>,
    footer: {
      className: 'btn btn-primary',
      value: 'Add',
      action: 'add_tab()' 
    }

  };



  render(){


    return (
      <div className="resource-pane" id="resource-pane">
                <div className="resource_pane_tab_head" id="resource_pane_tab_head">
                  <Modal
                    modalProps={this.newResourceModal}
                 /> 
                </div>
                <button
          onClick={this.onClickMe}
          type = "button"
        >
        Click me 
        </button>
                <div className="tab_body" id="tab_body"></div>   



            </div>

      );
  }
}

export default ResourcePane;
