import React, {Component} from 'react';
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
    };

    this.toggleResourcePane = this.toggleResourcePane.bind(this);
    this.confirmReset = this.confirmReset.bind(this);
  }

   toggleResourcePane(){
      console.log('toggleResourcePane');
  }

  confirmReset(){
    console.log("reset");
  }


  render() {
    return (

           <nav id="menu" className="menu">
          <a href="http://arg.napier.ac.uk/monkeypuzzle/">
                <header className="menu-header">
                  <span className="menu-header-title">MonkeyPuzzle</span>
                </header>
            </a>

            <section className="menu-section">
                <h3 className="menu-section-title">Interface</h3>
                <ul className="menu-section-list">            
                   <li>
                    <a href="#" id="toggle_resource">
                      <Button 
                        className="btn btn-default"
                        onClick={() => this.toggleResourcePane()}
                        title="Toggle the visibility of the resources pane"
                      >
                        Toggle Resource Pane
                      </Button>
                      </a>
                   </li>
                   <li>
                    <a href="#" id="clear_storage_button">
                      <Button
                        className="btn btn-danger"
                        onClick={() => this.confirmReset()}
                        title="Reset MonkeyPuzzle to her initial state"
                      >
                        Reset
                      </Button>
                    </a>
                   </li>
                   <li>
                     <a href="#" id="load_demo_argument_button">
                      <Button
                        className="btn btn-default"
                        onClick={() => this.loadDemoArgument()}
                        title="Load a demonstration SADFace document into MonkeyPuzzle"
                      >
                        Load Demo 
                      </Button>
                     </a>
                   </li>
                    </ul>
              </section>

              </nav>
    );
  }
}

class Button extends Component {
  render(){
    const {
      onClick,
      className = '',
      children = '',
      title
    } = this.props;

    return (
       <button
        onClick={onClick}
        className={className}
        title={title}
        type="button"
      >
        {children}
      </button>
    );
  }
}



export default App;
