import React, {Component} from 'react';
import Slideout from 'slideout';


import MainUI from './Components/MainUI';
import SlideIn from './Components/SlideIn';

import Modal from './Components/Modal';


import './CSS/monkeypuzzle.css';
//import './CSS/font-awesome.min.css';
import './CSS/cytoscape-context-menus.css';
//import './CSS/jquery.qtip.min.css';
//import './CSS/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './CSS/App.css';






class App extends Component{
	constructor(props){
		super(props);
		this.state = {
			tabs: [],
			last_number: 1,
			tab_id:"",


		};

		this.add_tab = this.add_tab.bind(this);
		this.toggleButton = this.toggleButton.bind(this);
		this.add_resource =this.add_resource.bind(this);
		this.add_resource_header = this.add_resource_header.bind(this);
		this.add_to_tabs_arr =this.add_to_tabs_arr.bind(this);
	}

	set_State(value) {
	    	this.setState({tab_id: value});
	    	console.log("set state "+ this.state.tab_id);

}

	add_resource(content) {
    /*
    Create a new resource dict using the supplied content string
    then add to the resourses list of the sadface doc

    Returns: the new resource dict
    */
    if (content) {
        var res = this.new_resource(content);
       // sd.resources.push(res);
        return res;
    }
}



add_to_tabs_arr = (tab_id, tab_number) => {
	this.setState(state => {
		const tabs = state.tabs.concat({tab_id:tab_id, tab_number: tab_number});
		console.log("add to tabs arr " + tabs);
		return {
			tabs,
		};
	});
};

createNewTab(){

}

 next_number(){
 	 this.setState({ last_number: this.state.last_number + 1})
 }




	add_tab(load_id=null){
		if (load_id == null) {
        var new_resource = this.add_resource(' ');
    	this.setState({
    		tab_id: new_resource.id
    	}, () => {
    		console.log("add tab tab_id " +this.state.tab_id);
    	//  add_resource_metadata(tab_id, 'title', '');
		//localStorage.setItem("state",JSON.stringify(get_sd()));

    		this.add_resource_header();
    	});


		} 
    	else { 
    		this.setState({
    		tab_id: load_id 
    		}, () => {
    			console.log("tab_id=load id: "+this.state.tab_id);
    			this.add_resource_header();
    		});
    	}

    	// 

  //  var resource_type_idx = document.getElementById("resource_type").options.selectedIndex;
  //  var resource_type_txt = document.getElementById("resource_type").options[resource_type_idx].text;
  //  if (resource_type_txt.toLowerCase() === "text") { add_text_resource_body(tab_id); }

  //  set_active_tab(tab_id+"_body");
   // return this.state.tab_id
    }





	add_resource_header() {
 	console.log("add Resource header "+ this.state.tab_id);
  	var tab_number = this.state.last_number;
	this.next_number();
  	this.add_to_tabs_arr(this.state.tab_id, tab_number);

  //  var resource_pane_tab_head = document.createElement("button");
    console.log("Show tabs"+ tab_number);
   // var node = document.createTextNode
  //  $(`
  //      <button id="`+ tab_id + `_btn" class="tablinks" 
   //         onclick="set_active_tab('` + tab_id + `_body')">` + 
   //         tab_number + `</button>
    //`);

   // $(".resource_pane_tab_head").append(resource_pane_tab_head);
}

	 new_resource(content) {
    /*
    Given the supplied content (String), create a new resource dict

    The arguments that SADFace describes are either constructed directly in a tool that writes
    them to this format, or else are sourced from elsewhere, e.g. an argumentative text or
    webpage, or else perhaps another medium, such as audio or video. Currently SADFace supports
    textual resources which are stored in the content key. Optionally a 
        "url":"some web location"
    pair can be added to the metadata to indicate a specific web location. Alternatively:
        "doi":"digital object identifier" - resolvable by dx.doi.org
        "magnet-link":"a torrent file"
        "isbn":"for books"
    Metadata may also store additional bibliographic or referencing/citation information
    as defined in bibtex formats.

    Returns: A dict representing the new SADFace resource
    */
    if (content) {
        var new_resource = {"id":this.new_uuid(), "content":content, "type":"text", "metadata":{}};
        return new_resource;
    }
}



//uuid function from https://stackoverflow.com/questions/105034/create-guid-uuid-in-javascript
 new_uuid() {
    /*
    Utility method to generate a new universally unique ID. Used througout to uniquely
    identify various items such as atoms, schemes, resources, & edges

    Returns: A string
    */
    return ([1e7]+-1e3+-4e3+-8e3+-1e11).replace(/[018]/g, c =>
        (c ^ crypto.getRandomValues(new Uint8Array(1))[0] & 15 >> c / 4).toString(16)
    );
}

 	componentDidMount() {
  	this.slideout = new Slideout({
    'panel': document.getElementById('panel'),
    'menu': document.getElementById('menu'),
    "fx": "ease",
    "side": "right",
    "duration": 500,
    "padding": 256,
    "tolerance": 70

  });
}


toggleButton(){
        this.slideout.toggle();
	}

	  newResourceModal = {
    trigger: {
      triggerText:'MMODALbutton',
      className: 'add_tab_button',
    },

    title: 'New Resource Pane',
    content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type">
                    <option>Text</option>
                </select>
              </div>,
    footer: {
      className: 'btn btn-primary',
      value: 'Add',
    }

  };



  render(){

    return (
    	<div>
    	{this.state.tab_id}
    	<button
    		onClick = {() => this.add_tab()}
    	>
    	Test Button
    	</button>

	    	 <Modal 
                modalProps = {this.newResourceModal}
                onClick = {() => this.add_tab()}
                />

    	<SlideIn />

    	<MainUI
    		toggleSlideIn={() => this.toggleButton()}
    		addTab = {() => this.add_tab()}
    		tabs = {this.state.tabs}
    		/>





   </div>

      );
  }
}






















export default App;
