import React, {Component} from 'react';
import Modal from './Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';

const _tabs = [
    {
        tab_id: 13213123123,
        tab_number:1,
        tab_body: "body1"
    },
    {
        tab_id: 1322423413123123,
        tab_number:2,
        tab_body: "body2"
    },
    {
        tab_id: 132242341342342343123123,
        tab_number:3,
        tab_body: "body3"
    }
];

class ResourcePane extends Component {
  constructor(props){
    super(props);
	};
 		render(){
 			return(
 				<ResourcePaneHead 
	 				content={_tabs}
	 				addTab = {this.props.add_tab}
	 				tabs = {this.props.tabs}

				/>  
 			);
 		}
}

class ResourcePaneHead extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: this.props.content,
            activeTab: 0
        }
    };

    handleClick(index) {
        this.setState({
            activeTab: index
        });
    }

    render() {
    	const{
    		addTab

    	} =this.props;
        return (
            	<div className="resource-pane" id="resource-pane">

		                <TabsNav 
		                  links={this.state.items} 
		                  handleClick={this.handleClick.bind(this)} 
		                  active={this.state.activeTab}
		                  addTab={addTab} 
		                />
		            <div className="tab_body" id="tab_body">

		                <TabsContent 
		                  content={this.state.items} 
		                  active={this.state.activeTab} 
		                  />
		            </div>


	            </div>
        );
    }
}

class TabsNav extends Component {
	constructor(props){
    super(props);
    this.state ={
    }
  };
	  newResourceModal = {
    trigger: {
      triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
      className: 'add_tab_button',
      triggerId: "add_tab_button",
    },
    id:"resource_pane_selection_modal",
    title: 'New Resource Pane',
    content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type">
                    <option>Text</option>
                </select>
              </div>,
    footer: {
      className: 'btn btn-primary',
      value: 'Add',
     // action: this.add_tab() 
    }

  };

    handleClick(index) {
        this.props.handleClick(index);
    }

    render() { 

        let buttons = this.props.links.map((item, i) => {
            return (<button  onClick={this.handleClick.bind(this, i)} key={i} className={this.props.active === i ? "tablinks active" : "tablinks"}>{item.tab_number}</button>);
        });

        return (
	 		<div className="resource_pane_tab_head" id="resource_pane_tab_head">
                 <Modal
                 	modalProps={this.newResourceModal}
                 	onClick = {this.props.add_tab}

                 />
                {buttons}
            </div>
        );       
    }
}


class TabsContent extends React.Component {
    render() {            
        let tabs = this.props.content.map((item, i) => {
            return (
            	<div className="TabsContent-tabPane" key={i} style={{display: this.props.active === i ? 'block' : 'none'}} >
				<div id={item.tab_id + "_body"} className="resource_pane_tab_content">
                <form>
                    <div className="form-group">
                    		<div>{item.tab_id}</div>

                            <button type="button" className="btn btn-default" /*onclick="remove_tab()"*/ title="Remove this tab from the resource pane">
                                <i className="fa fa-trash fa-fw fa-lg"></i>
                            </button>
                            <input type="file" id={"load" + item.tab_id} style={{display: "none"}} accept=".txt" /*onchange="filemanager('load','txt','` + tab_id + `')"*//>
                            <button type="button" id={"load" + item.tab_id + "_btn"} className="btn btn-default" /*onclick="$('#load` + tab_id + `').trigger('click');" */title="Load a text file into this resource tab">
                                <i className="fa fa-upload fa-fw fa-lg"></i>
                            </button>
                            <button type="button" className="btn btn-default" /*onclick="filemanager('save','txt','` + tab_id + `')"*/ title="Save this resource tab to a text file">
                                <i className="fa fa-download fa-fw fa-lg"></i>
                            </button>
                            <button id="toggle_edit_lock_button" type="button" className="btn btn-default" title="Toggle editability of the content area" /*onclick="toggle_edit_lock();"*/>
                                <i id={"toggle_edit_lock_icon_" + item.tab_id} className="fa fa-lock fa-fw fa-lg"></i>
                            </button>
                            <button type="button" className="btn btn-default" title="Add node from text selection"/* onclick="new_atom_txt_resource_button();"*/>
                                <i className="fa fa-puzzle-piece fa-fw fa-lg"></i>
                            </button>
                    </div>
                    <div className="form-group">
                        <label>Title</label>
                        <textarea id={"title_" + item.tab_id} type="text" rows="1" style={{resize: "none"}} className="form-control" placeholder="The name of this resource..." /*onchange="change_title('` + tab_id + `')"*/></textarea> 
                        <label>Content</label>
                        <div id="textarea">
                            <textarea id={item.tab_id} className="form-control" placeholder="Enter your source text here..." rows="20" /*onchange="change_textarea('` + tab_id + `')" onfocus="set_focus(this)"*/ style={{resize: "vertical", minHeight: "35px"}} readOnly></textarea>
                        </div>  
                    </div> 
                </form>
              </div>
            </div>
            );
        });

        return (
            <div className="TabsContent">
                {tabs}
            </div>
        );
    }   
}





export default ResourcePane;
