import React, { Component } from 'react';
import Modal from './Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons'
import SlideInTrigger from './SlideInTrigger';




class MainUI extends Component {
  constructor(props){
    super(props);
    this.state ={
      current_tab:'0',
    }
  };

  newResourceModal = {
    trigger: {
      triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
      className: 'add_tab_button',
      triggerId: "add_tab_button",
    },
    id:"resource_pane_selection_modal",
    title: 'New Resource Pane',
    content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type">
                    <option>Text</option>
                </select>
              </div>,
    footer: {
      className: 'btn btn-primary',
      value: 'Add',
     // action: this.add_tab() 
    }

  };

  set_active_tab(tab_id){
  if(this.props.tabs.length > 0){
    console.log("set active tabs "+tab_id);
     this.setState({current_tab:  tab_id.substring(0, tab_id.indexOf("_body")) });
     var i, tabcontent, tablinks;
     /* tabcontent = document.getElementsByClassName("resource_pane_tab_content");
        for (i = 0; i < tabcontent.length; i++) {
            tabcontent[i].style.display = "none";
       }
      tablinks = document.getElementsByClassName("tablinks");
        for (i = 0; i < tablinks.length; i++) {
           tablinks[i].className = tablinks[i].className.replace("active", "");
      }
    if (document.getElementById(tab_id)) {
        document.getElementById(tab_id).style.display = "block";
        document.getElementById(this.state.current_tab+"_btn").className += " active";
    }*/

  }
}


  render() {
    const {
      toggleSlideIn,
      addTab,
    //  tabs

    } = this.props;

    return (
            /* Main UI: START */

    <main id="panel" className="panel">
    <h1>hello {this.state.current_tab}</h1>
    <div className="ui-container">
              <Modal 
                modalProps = {this.newResourceModal}
                onClick = {addTab}
                />
                <div> 
             {/*} {tabs.map(item =>{
                const onHandleSetActiveTab = () =>
                this.set_active_tab(item.tab_id+'_body');

                  return (
                    <div>
                    </div>

                  );
                })  } */}                                                  


              </div>


        <div className="splitter" id="splitter">
        </div>

        <div className="visualisation-pane">
            <div id="navbar">

                <SlideInTrigger
                  className="btn-hamburger toggle-button"
                  onClick={toggleSlideIn}
                />
            </div>
           {/*} <div id="cy" onDrop="drop_handler(event);" onDragover="dragover_handler(event);"></div>*/}
        </div>
    </div>
    </main>

   /* Main UI: END */
    );
  }
}





class TabsNav extends Component {
    handleClick(index) {
        this.props.handleClick(index);
    }

    render() {
        let activeStyles = {
            backgroundColor: 'deepskyblue',
            borderColor: 'deepskyblue',
            color: '#fff'
        }

        let buttons = this.props.links.map((item, i) => {
            return (<button className="TabsNav-btn" onClick={this.handleClick.bind(this, i)} key={i} style={this.props.active === i ? activeStyles : {}}>{item.tab_number}</button>);
        });

        return (
            <nav className="TabsNav">
                {buttons}
            </nav>
        );       
    }
}


class TabsContent extends React.Component {
    render() {            
        let tabs = this.props.content.map((item, i) => {
            return (<div className="TabsContent-tabPane" key={i} style={{display: this.props.active === i ? 'block' : 'none'}} dangerouslySetInnerHTML={{__html: item.tab_body}} />);
        });

        return (
            <div className="TabsContent">
                {tabs}
            </div>
        );
    }   
}

class Tabs extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: this.props.content,
            activeTab: 0
        }
    }

    handleClick(index) {
        this.setState({
            activeTab: index
        });
    }

    render() {
        return (
            <div className="Tabs">
                <TabsNav 
                  links={this.state.items} 
                  handleClick={this.handleClick.bind(this)} 
                  active={this.state.activeTab} 
                />
                <TabsContent 
                  content={this.state.items} 
                  active={this.state.activeTab} 
                  />
            </div>
        );
    }
}







export default MainUI;
