import React, {Component} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons';
//Import Components
import Modal from './Modal';
import { newResourceModal} from './ModalData.jsx';



import Button from 'react-bootstrap/Button';



class ResourcePane extends Component {
    constructor(props) {
        super(props);
        this.state = {

        }
        this.handlefileManager =this.handlefileManager.bind(this);
        this.remove_all_tabs = this.remove_all_tabs.bind(this);
        this.handleAdd = this.handleAdd.bind(this);
        this.returnType =this.returnType.bind(this);
        this.handlegetType = this.handlegetType.bind(this);
        this.handleNew_atom_txt_resource_button = this.handleNew_atom_txt_resource_button.bind(this);


    };
/*
    setActiveTab(value){
        this.setState({
            activeTab: value
        }, function (){
            console.log(this.state.activeTab, 'active');
        });
    }
*/

returnType(){
    var resource_type_idx = document.getElementById("resource_type").options.selectedIndex;
    var resource_type_txt = document.getElementById("resource_type").options[resource_type_idx].text;
    let type = resource_type_txt.toLowerCase()
    this.props.getType(type);

}

handleAdd(){


    this.props.addTab();
   console.log("handleADD");
}

searchTabs(tab){

}

remove_all_tabs(){
    console.log("remove tabs resource pane")
}





handlefileManager(){
    this.props.fileManager()
}
componentDidMount(){
    this.setState({activeTab: this.props.activeTab})
    console.log(this.state.activeTab, 'active tabs');
}

    componentDidUpdate(prevProps) {
  // Typical usage (don't forget to compare props):
  if (this.props.remove_all_tabs !== prevProps.remove_all_tabs) {
    this.remove_all_tabs();
  }
}

handlegetType(e){
    let type = e.target.value;
    console.log(type,'resourcePane type');

}

handleNew_atom_txt_resource_button(){
     if(this.props.focused != null || this.props.focused != undefined){
        if(this.props.focused.parentNode.id == "textarea"){
            var elem = document.getElementById(this.props.focused.id);

            var selection = elem.value.substring(elem.selectionStart, elem.selectionEnd);
            console.log(selection, 'selection');
            console.log(elem, 'elem');
          //  if(selection != null || selection != undefined){
            //   add_new_atom_node(selection);
             //   elem.selectionStart = elem.selectionEnd;
             //   clear_focus();
           // }
        }
    }
    else { console.log("Not a valid text source") }

}





render() {
    const{
        activeTab


    } =this.props;
    var tabNumber = 1;


    const newResourceModal = {
  trigger: {
    triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
    className: 'add_tab_button',
    triggerId: "add_tab_button",
  },
  id:"resource_pane_selection_modal",
  title: 'New Resource Pane',
  content:  <div className="modal-body">
  <p>Select a resource type</p>
  <select className="form-control" id="resource_type" onChange={this.handlegetType}>
  <option>Text</option>
    <option>none</option>
  </select>
  </div>,
  footer: {
    buttonNo: 2,
    button1: {
      className: "btn btn-secondary",
      value: "Close"
    },
    button2: {
      className: "btn btn-primary",
      value: "Add"
    }
  }
};


    return ( 
        <div className="resource-pane" id="resource-pane" style={{display: this.props.showResourcePane ? "block" : "none"}}> 
            <div className="resource_pane_tab_head" id="resource_pane_tab_head">
                        <Modal
                          modalProps = {newResourceModal}  
                            onClick = {this.returnType} 
                         />

                        {
                            this.props.tabs.map((item, i)=> {
                                const onSetActiveTab = () =>
                                    this.props.setActiveTab(item.id);
                                return(
                                    <button  
                                        key={item.id + '_btn'}
                                        onClick={onSetActiveTab} 
                                        className={this.props.current_tab === item.id ? "tablinks active" : "tablinks"}
                                        id={item.id+"_btn"}
                                    >
                                        {item.tab_number}
                                    </button>

                                );
                            })
                        }
                    </div>



                    <div className="tab_body" id="tab_body">
                        {this.props.tabs.map((item,i) => {
                            if(item.type === "text"){
                            this.myRef = React.createRef();
                            this.contentRef = React.createRef();
                            this.titleRef = React.createRef();

                            const handleRemove = () =>
                                this.props.remove_tab(i);

                            const clickLoad =() => {

                                this.myRef.current.click();

                            }

                            const handleFileUpload = () =>{
                            var files = this.myRef.current.files;
                            this.props.fileManager('load','txt',  item.tab_id ,files);

                            }
                            const handlefileManager = () =>{

                                this.props.fileManager('save', 'txt', item.tab_id); 
                            }
                            const handleChange_title = (e) =>{
                                var titleValue = this.titleRef.current.value;
                                this.props.change_title(item.tab_id, titleValue);
                            }

                            const handleChange_content = () =>{
                                var contentValue = this.contentRef.current.value;
                                this.props.change_content(item.tab_id, contentValue);
                            }

                            return(
                                <TabBody
                                    id={item.id}
                                    key={item.id + '_bdy'}
                                    i={i}
                                    activeTab={this.state.activeTab}
                                    handleremove={handleRemove}
                                    fileManager={handlefileManager}
                                    style={{display: this.props.current_tab === item.id ? 'block' : 'none'}}
                                    clickLoad={clickLoad}
                                    titleValue={item.metadata.title}
                                    contentValue = {item.content}

                                    handleFileUpload={handleFileUpload}
                                    handleChange_title={handleChange_title}
                                    handleChange_content={handleChange_content}

                                    loadRef={this.myRef}
                                    titleRef={this.titleRef}
                                    contentRef={this.contentRef}
                                    set_focus={this.props.set_focus}
                                    handleNew_atom_txt_resource_button = {this.handleNew_atom_txt_resource_button}
                                />
                            )
                        }
                        })}
                    </div>
                    </div>
                    );

}
}






class TabBody extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            readOnly: true,
        }
        this.toggle_edit_lock = this.toggle_edit_lock.bind(this);
        this.handleSet_focus = this.handleSet_focus.bind(this);


    }

    toggle_edit_lock(){
        this.setState({readOnly: !this.state.readOnly})

    }

    handleSet_focus(element){
    let focused = document.getElementById(element.props.id);//.id;
    this.props.set_focus(focused);
}





    render() { 
        const {
          tabs,
          id,
          i,
          activeTab,
          handleremove,
          handleLoad,
          fileManager,
          style,
          loadTrigger,
          ref,
          clickLoad,
          contentValue,
//element references
loadRef,
titleRef,
contentRef,

handleFileUpload,
handleChange_title,
handleChange_content,
set_focus,
handleNew_atom_txt_resource_button,


}=this.props



return (
    <div id={id+"_body"} className="resource_pane_tab_content" style={style}>
    <form>
    <div className="form-group">

    <Button 
    type="button" 
    className="btn btn-default" 
    title="Remove this tab from the resource pane"
    onClick={handleremove}
    variant="light"
    >
    <i className="fa fa-trash fa-fw fa-lg"></i>
    </Button>
    <input 
    type="file" 
    id={"load" + id} 
    style={{display: "none"}} 
    accept=".txt" 
    ref={loadRef}
    onChange= {handleFileUpload}
    />
    <Button 
    type="button" 
    id={"load" + id + "_btn"} 
    className="btn btn-default" 
    onClick={clickLoad}

    title="Load a text file into this resource tab"
    variant="light"
    >
    <i className="fa fa-upload fa-fw fa-lg"></i>

    </Button>
    <Button 
    type="button" 
    className="btn btn-default" 
    onClick={fileManager}
    title="Save this resource tab to a text file"
    variant="light"
    >
    <i className="fa fa-download fa-fw fa-lg"></i>
    </Button>
    <Button id="toggle_edit_lock_button" 
    type="button" 
    className="btn btn-default" 
    title="Toggle editability of the content area" 
    onClick={ this.toggle_edit_lock}
    variant="light"
    >
    <i id={"toggle_edit_lock_icon_" + id} 
    className={(this.state.readOnly) ? "fa fa-lock fa-fw fa-lg" : "fa fa-unlock fa-fw fa-lg"}></i>
    </Button>
    <Button 
    type="button" 
    className="btn btn-default" 
    title="Add node from text selection"
    onClick={handleNew_atom_txt_resource_button}
    variant="light"
    >
    <i className="fa fa-puzzle-piece fa-fw fa-lg"></i>
    </Button>
    </div>
    <div className="form-group">
        <label>Title</label>
        <textarea 
            id={"title_"+id} 
            type="text" 
            rows="1" 
            style={{resize: "none"}} 
            className="form-control" 
            placeholder="The name of this resource..." 
            onChange={handleChange_title}
            ref={titleRef}
            value={this.props.titleValue}
            >
        </textarea> 
    <label>Content</label>
    <div id="textarea">
    <textarea 
    id={id} 
    className="form-control" 
    placeholder="Enter your source text here..." 
    rows="20" 
    ref={contentRef}
    onChange={handleChange_content} 
    onFocus={() => this.handleSet_focus(this)} 
    style={{resize: "vertical", minHeight: "35px"}} 
    readOnly={this.state.readOnly}
    value= {contentValue}>

    </textarea>
    </div>  
    </div> 
    </form>
    </div>
    );
}


}   






export default ResourcePane;
