import React, {Component} from 'react';
import Slideout from 'slideout';


import MainUI from './Components/MainUI';
import SlideInTrigger from './Components/SlideInTrigger';

import Modal from './Components/MainUI';


import './CSS/monkeypuzzle.css';
//import './CSS/font-awesome.min.css';
import './CSS/cytoscape-context-menus.css';
//import './CSS/jquery.qtip.min.css';
//import './CSS/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './CSS/App.css';
import {Button} from 'react-bootstrap';


var FontAwesome = require('react-fontawesome');








class App extends Component{
	toggleButton(){
        this.slideout.toggle();
	}

 	componentDidMount() {
  	this.slideout = new Slideout({
    'panel': document.getElementById('panel'),
    'menu': document.getElementById('menu'),
    "fx": "ease",
    "side": "right",
    "duration": 500,
    "padding": 256,
    "tolerance": 70

  });
}

  render(){

    return (
    	<div>
      {/*Slidein Panel: START*/}
    <nav id="menu" className="menu">
    <a href="http://arg.napier.ac.uk/monkeypuzzle/" target="_blank">
        <header className="menu-header">
          <span className="menu-header-title">MonkeyPuzzle</span>
        </header>
    </a>

    <section className="menu-section">
        <h3 className="menu-section-title">Interface</h3>
        <ul className="menu-section-list">            
           <li> <a href="#" id="toggle_resources"><button className="btn btn-default" onClick="toggle_resource_pane()" title="Toggle the visibility of the resources pane">Toggle Resource Pane</button></a></li>
            <li><a href="#" id="clear_storage_button"><button className="btn btn-danger" onClick="$('#confirm_reset_modal').modal('show');" title="Reset MonkeyPuzzle to her initial state">Reset</button></a></li>
            <li><a href="#" id="load_demo_argument_button"><button className="btn btn-default" onclick="load_demo_argument()" title="Load a demonstration SADFace document into MonkeyPuzzle">Load Demo</button></a></li>
            <li><div style={{marginLeft: "20px", marginRight: "60px"}}><textarea id="analyst_name_textarea" type="text" rows="1" cols="20" maxLength="40" style={{resize: "none"}} class="form-control"  placeholder="Name" title="Type your name here..." onchange="set_analyst_name();"></textarea></div></li>
            <li><div style={{marginLeft: "20px", marginRight: "60px"}}><textarea id="analyst_email_textarea" type="text" rows="1" cols="20" maxLength="40" style={{resize: "none"}} class="form-control"  placeholder="Email" title="Type your email address here..." onchange="set_analyst_email();"></textarea></div></li>
      </ul>
      </section>


    <section class="menu-section">
        <h3 class="menu-section-title">Export</h3>
        <ul class="menu-section-list">
            <li><div style={{marginLeft: "20px", marginRight: "60px"}}><textarea id="export_filename" type="text" rows="1" cols="20" maxLength="20" style={{resize: "none"}} className="form-control"  placeholder="Filename" title="The name for the exported file"></textarea></div></li>

            <li><a href="#" id="export_button"><button class="btn btn-default" onclick="download_sadface()" title="Export the current analysis to a SADFace formatted JSON document">SADFace</button></a></li>
            <li><a href="#" target="_blank" id="download_png"><button class="btn btn-default" title="Export the current analysis to a PNG image" onclick="download_png()">PNG</button></a></li>
            <li><a href="#" target="_blank" id="download_jpg"><button class="btn btn-default" title="Export the current analysis to a JPG image" onclick="download_jpg()">JPG</button></a></li>
      </ul>
      </section>

    <section className="menu-section">
        <h3 className="menu-section-title">Import</h3>
        <ul className="menu-section-list">
			<span className="upload-span">
				<a rel="ignore">
					<div style={{display: "inline-block"}}>
						<input id="loadJSON" style={{display: "none"}} accept=".json" role="button" tabIndex="-1000" type="file" className="upload-button" onChange="filemanager('load','json',null,null,null)"/>
                        <input type="button" className="btn btn-default" value="SADFace" onclick="document.getElementById('loadJSON').click();" title="Load a SADFace formatted JSON document into MonkeyPuzzle"/>
					</div>
				</a>
			</span>

        </ul>
    </section>

    <section class="menu-section">
        <h3 class="menu-section-title">Documentation</h3>
        <ul class="menu-section-list">            
            <a href="http://arg.napier.ac.uk/page/project/monkeypuzzle/" id="a">About MonkeyPuzzle</a>
            <a href="http://arg.napier.ac.uk/page/project/monkeypuzzle/quickstart/" id="a">Quick-start</a>
            <a href="https://github.com/ARG-ENU/monkeypuzzle_web" id="a">Code Repository</a>
            <a href="https://github.com/ARG-ENU/monkeypuzzle_web/issues" id="a">Issue Tracker</a>
            <a href="https://github.com/ARG-ENU/monkeypuzzle_web/blob/master/LICENSE" id="a">Licensing</a>
      </ul>
      </section>
    </nav>
   {/* Slidein Panel: END */}


    {/* Main UI: START */} 

    <main id="panel" class="panel">
    <div class="ui-container">
        <div class="resource-pane" id="resource-pane">
            <div class="resource_pane_tab_head" id="resource_pane_tab_head">
                <button id="add_tab_button" class="add_tab_button" onClick="$('#resource_pane_selection_modal').modal('show');"> 
                    <i class="fa fa-plus-square fa-lg" style={{color: "#cbc5c1"}}></i>
                </button>
            </div>
            <div class="tab_body" id="tab_body"></div>        
        </div>

        <div class="splitter" id="splitter">
        </div>

        <div class="visualisation-pane">
            <div id="navbar">
                <button 
                	className="btn-hamburger toggle-button"
                	onClick={() => this.toggleButton()}
				>
                <i class="fa fa-bars fa-fw fa-lg" style={{color: "#cbc5c1"}}></i>
                </button>
            </div>
            <div id="cy" ondrop="drop_handler(event);" ondragover="dragover_handler(event);"></div>
        </div>
    </div>
    </main>

   {/* Main UI: END */}
   </div>

      );
  }
}






















export default App;
