import React, {Component} from 'react';
import './CSS/monkeypuzzle.css';
//import './CSS/font-awesome.min.css';
import './CSS/cytoscape-context-menus.css';
//import './CSS/jquery.qtip.min.css';
//import './CSS/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './CSS/App.css';
import {Modal, Button, Row, Col, Form, ButtonToolbar} from 'react-bootstrap';


//var FontAwesome = require('react-fontawesome');
const interfaceDivStyle = {
  marginLeft:'20px',
  marginRight:'60px'
}

const textAreaStyle = {
  resize: 'none'
}

class App extends Component{
  constructor(props){
    super(props);
      this.state = {
        addShowModal: false,
      }
    }


  render(){
        let addModalClose = () => this.setState({addModalShow:false});

    return (
      <div>
        <Interface />
        <MainUI />
        <Modal />

      </div>
      );
  }
}

class MainUI extends Component{


  render() {
    return (
      <main id="panel" className="panel">
    <div className="ui-container">
        <div className="resource-pane" id="resource-pane">
            <div className="resource_pane_tab_head" id="resource_pane_tab_head">
                <Button 
                  id='add_tab_button'
                  className="add_tab_button"
                  onClick={() => this.setState({addModalShow: true})} 
                   // <i clclassNameass="fa fa-plus-square fa-lg" /*style="color: #cbc5c1;"*/></i>
                >

                </Button>
            </div>
            <div className="tab_body" id="tab_body"></div>        
        </div>

        <div className="splitter" id="splitter">
        </div>

        <div className="visualisation-pane">
            <div id="navbar">
                <Button 
                  className='btn-hamburger toggle-button'
               // <i className="fa fa-bars fa-fw fa-lg" /*style="color: #cbc5c1;"*/></i>
                >

                </Button>
            </div>
            <div id="cy" ondrop="drop_handler(event);" ondragover="dragover_handler(event);"></div>
        </div>
    </div>
    </main>
      );
  }
}






class Interface extends Component {
  constructor(props){
    super(props);
    this.state = {
    };

    this.toggleResourcePane = this.toggleResourcePane.bind(this);
    this.confirmReset = this.confirmReset.bind(this);
    this.loadDemoArgument = this.loadDemoArgument.bind(this);
    this.set_analyst_name = this.set_analyst_name.bind(this);
    this.set_analyst_email = this.set_analyst_email.bind(this);
    this.filemanger = this.filemanger.bind(this);
    this.loadSADFace = this.loadSADFace.bind(this);
  }

   toggleResourcePane(){
      console.log('toggleResourcePane');
  }

  confirmReset(){
    console.log("reset");
    //$('#confirm_reset_modal').modal('show');"
  }

  loadDemoArgument(){
    console.log('load');
  }

  set_analyst_name(){
    console.log('set name');
  }

  set_analyst_email(){
    console.log('set email');
  }

  filemanger(){
    console.log('file manager');
  }
  loadSADFace(){
   // "document.getElementByID('loadJSON').click();"
   console.log('loadSADFace');
  }

  render() {
    return (

           <nav id="menu" className="menu">
          <a href="http://arg.napier.ac.uk/monkeypuzzle/">
                <header className="menu-header">
                  <span className="menu-header-title">MonkeyPuzzle</span>
                </header>
            </a>

            <section className="menu-section">
                <h3 className="menu-section-title">Interface</h3>
                <ul className="menu-section-list">            
                   <li>
                    <a href="#" id="toggle_resource">
                      <MyButton 
                        className="btn btn-default"
                        onClick={() => this.toggleResourcePane()}
                        title="Toggle the visibility of the resources pane"
                      >
                        Toggle Resource Pane
                      </MyButton>
                      </a>
                   </li>
                   <li>
