class MyButton extends Component {
  render(){
    const {
      onClick,
      className = '',
      children = '',
      title
    } = this.props;

    return (
       <button
        onClick={onClick}
        className={className}
        title={title}
        type="button"
      >
        {children}
      </button>
    );
  }
}

class TextArea extends Component {
  render(){
    const {
      className = '',
      title = '',
      placeholder,
      id,
      rows,
      cols,
      maxLength,
      onChange,
      children,
      style

    } = this.props;

    return (
      <textarea
      className={className}
      title={title}
      placeholder={placeholder}
      id={id}
      rows={rows}
      cols={cols}
      maxLength = {maxLength}
      onChange = {onChange}
      type="textarea"
      style={style}
      >
        {children}
      </textarea>
      );
  }
}

