    initialise_monkeypuzzle(){
          //load diagram if there is one in localStorage
        if (localStorage.getItem("state"))
        {
            this.loadJSON(localStorage.getItem("state"));
            this.initCytoscape();
        //else use default
        } else
        {
            current_sadface_doc = this.setDefault();
            localStorage.setItem("state", current_sadface_doc);
         //   cy_data = export_cytoscape({import_json(current_sadface_doc)});
            this.initCytoscape();
        }
    }

setDefault(){
    this.setState({
        analyst_email: "Default Analyst Email",
        analyst_name: "Default Analsyst Name",
        created: this.props.now(),
        edges:[],
