                    targetNode.remove();
                    cy.add([
                        {group: "nodes", data: {id: scheme_id.toString(),
                            content: scheme_content, typeshape: "diamond" }, classes: "scheme-label", locked: false, position: position}
                    ]);
                    var edge1 = this.add_edge(source_id, scheme_id);
                    var edge2 = this.add_edge(scheme_id, target_id);
                    cy.add([
                      { group: "edges", data: { id: edge1.id.toString(), source: source_id, target: scheme_id } },
                      { group: "edges", data: { id: edge2.id.toString(), source: scheme_id, target: target_id } }
                    ]);
                } else {
                    targetNode.remove();
                    var edge = this.add_edge(source_id, target_id);
                    cy.add([
                      { group: "edges", data: { id: edge.id.toString(), source: source_id, target: target_id } }
                    ]);
                }
                this.props.update_local_storage();
            } else {
                targetNode.remove();
            }
        }
    });
    }


    componentDidMount(){
        this.initialise_monkeypuzzle();
        this.setState(
            {created_date: this.now,
             document_id: this.props.new_uuid,
             default_sadface_doc: "{\"analyst_email\":\""+analyst_email+"\",\"analyst_name\":\""+analyst_email+"\",\"created\":\""+this.state.created_date+"\",\"edges\":[],\"edited\":\""+this.state.edited_date+"\",\"id\":\""+this.state.document_id+"\",\"metadata\":{},\"nodes\":[],\"resources\": []}",

            }
            )
    }

    render(){
        return(
            <div id="cy" ondrop="drop_handler(event);" ondragover="dragover_handler(event);"></div>
        )
    }
}

