import React, { Component } from 'react';

import cytoscape from 'cytoscape';
import cola from 'cytoscape-cola';
import edgehandles from 'cytoscape-edgehandles';
import contextMenus from 'cytoscape-context-menus';
import 'cytoscape-context-menus/cytoscape-context-menus.css';

import $ from 'jquery';


import {import_json,export_cytoscape,add_atom, sd,add_scheme, add_edge, get_atom, now, new_uuid, update_atom_text, get_sd, delete_edge, update, add_source} from '../lib/Sadface'; 
//Import Components
import Modal from  './Modal';


cytoscape.use( edgehandles );
cytoscape.use(contextMenus, $);
cytoscape.use( cola );




var cy = null;
var cm = null;
var selected = [];
var position = null;
var layout = null;
var running = false;
var json;
var created_date = now();
var edited_date = now();
var analyst_email = "Default Analyst Email";
var analyst_name = "Default Analsyst Name";
var document_id = new_uuid();
var default_sadface_doc = "{\"analyst_email\":\""+analyst_email+"\",\"analyst_name\":\""+analyst_email+"\",\"created\":\""+created_date+"\",\"edges\":[],\"edited\":\""+edited_date+"\",\"id\":\""+document_id+"\",\"metadata\":{},\"nodes\":[],\"resources\": []}";
var current_sadface_doc = null;
var focused = null;
var undo_stack = [];
var redo_stack = [];
var edit_atom = null;
var resource_pane_viewable_state = true;



class Cytoscape extends Component {
  constructor(props){
        super(props);
        //create Refs
        this.addAtomTextRef = React.createRef();
        this.newSchemeRef = React.createRef();
        this.editContentRef =React.createRef();

        this.state={
         png:'',
         jpg:'',
         NewAtomText: '',
         modalValue: ' ',
        }
            this.initialise_monkeypuzzle = this.initialise_monkeypuzzle.bind(this);
            this.initCytoscape = this.initCytoscape.bind(this);
            this.build_cola_layout = this.build_cola_layout.bind(this);
            this.edit_atom_content = this.edit_atom_content.bind(this);
            this. delete_nodes =this.delete_nodes.bind(this);
            this.update_local_storage = this.update_local_storage.bind(this);
            this.redraw_visualisation = this.redraw_visualisation.bind(this);
            this.undo = this.undo.bind(this);
            this.redo = this.redo.bind(this);
            this.merge_nodes = this.merge_nodes.bind(this);
            this.delete_atom =this.delete_atom.bind(this);
            //    this.dragover_handler = this.dragover_handler.bind(this);

            this.add_new_atom_node = this.add_new_atom_node.bind(this);
    }

    initialise_monkeypuzzle() {

    //load diagram if there is one in localStorage
    if (localStorage.getItem("state"))
    {
        console.log(localStorage.getItem("state"), 'localstorage')
        this.props.loadJSON(localStorage.getItem("state"));
       // this.initCytoscape();
    //else use default
    } else
    {
        current_sadface_doc = default_sadface_doc;
        localStorage.setItem("state", current_sadface_doc);
        console.log(current_sadface_doc, 'current sadface');
        //this.props.setCyData(export_cytoscape(import_json(current_sadface_doc)));
        this.initCytoscape();
    }
}

    initCytoscape(){
        console.log("initCyto");
        //change cy to state of APP
        cy = cytoscape({
            container: document.getElementById('cy'),
            elements: JSON.parse(this.props.cy_data),

            style:[
                {   selector: "node", 
                    style: {
                        "label": "data(content)",
                        "text-opacity": 0.7,
                        "width" : "auto",
                        "height" : "auto",
                        "text-valign": "bottom",
                        "text-halign": "right",
                        "text-outline-color": "#eee",
                        "text-outline-width": 1
                    }
                },

                {   selector: "[typeshape]", 
                    style: {
                        "shape":"data(typeshape)"
                    }
                },

                {   selector: "node[typeshape='diamond']", 
                    style: {
                        'background-color': '#CC9900'
                    }
                },

                {   selector: "node[typeshape='diamond'][content='Conflict']",
                    style: {
                        'background-color': 'red'
                    }
                },

                {   selector: "node[typeshape='diamond'][content='Support']",
                    style: {
                        'background-color': 'green'
                    }
                },

                {   selector: "edge", 
                    style: {
                        "line-color": "#9dbaea",
                        "target-arrow-shape": "triangle",
                        "target-arrow-color": "#9dbaea",
                        "curve-style": "bezier"
                    }
                },


                {   selector: ":selected", 
                    style: {
                        "border-width":"1",
                        "border-color":"black",
                        "background-color": "#3399CC"
                    }
                },


                {   selector: ".atom-label", 
                    style:{
                        "text-wrap": "wrap",
                        "text-max-width": 160
                    }
                },

                {   selector: ".scheme-label", 
                    style:{
                        "text-wrap": "wrap",
                        "text-max-width": 160
                    }
                },
                {


              selector: '.eh-handle',
              style: {
                'background-color': 'orange',
                'width': 12,
                'height': 12,
                'shape': 'ellipse',
                'overlay-opacity': 0,
                'border-width': 12, // makes the handle easier to hit
                'border-opacity': 0,
                'toggleOffOnLeave': true,
              }
            }
            ],

            boxSelectionEnabled: false,
            autounselectify: false,
            selectionType: "single",
            minZoom: 0.1,
            maxZoom: 1.5

        });//close cy = cytoscape()



    layout = this.build_cola_layout();
    layout.run();

    let defaults = {
                handleColor: "pink",


    }



    cy.edgehandles({
        toggleOffOnLeave: true,
        handleNodes: "node",
        handleSize: 10,
        handleColor: "#DF0085",
        handleHitThreshold: 8,
        handleLineWidth: 5,
        //handleLineType: "flat",
        handleOutlineColor: "pink",
        edgeType: function(){ return "flat"; },
        complete: function(event, sourceNode, targetNode, addedEles){
            if (targetNode.length !== 0) {
                var source_id = targetNode[0].source().id();
                var target_id = targetNode[0].target().id();

                //get the mid point between source node and target node
                var source_position = targetNode[0].source().position();
                var target_position = targetNode[0].target().position();

                position = {};
                position.x = ((source_position.x + target_position.x)/2);
                position.y = ((source_position.y + target_position.y)/2);

                if (targetNode[0].source().data().type == "atom" && targetNode[0].target().data().type == "atom")
                {
                    var scheme = add_scheme("Support");
                    var scheme_id = scheme.id;
                    var scheme_content = scheme.name;
                    //remove the automatically generated edge
                    targetNode.remove();
                    cy.add([
                        {group: "nodes", data: {id: scheme_id.toString(),
                            content: scheme_content, typeshape: "diamond" }, classes: "scheme-label", locked: false, position: position}
                            ]);
                    var edge1 = add_edge(source_id, scheme_id);
                    var edge2 = add_edge(scheme_id, target_id);
                    cy.add([
                      { group: "edges", data: { id: edge1.id.toString(), source: source_id, target: scheme_id } },
                      { group: "edges", data: { id: edge2.id.toString(), source: scheme_id, target: target_id } }
                      ]);
                } else {
                    targetNode.remove();
                    var edge = add_edge(source_id, target_id);
                    cy.add([
                      { group: "edges", data: { id: edge.id.toString(), source: source_id, target: target_id } }
                      ]);
                }
                this.update_local_storage();
            } else {
                targetNode.remove();
            }
        }.bind(this)
    });
    //edgehandles

    /*
     *
     * Set up context menus
     *
     * */
     cm = cy.contextMenus({
       menuItems: [
       {
        id: "edit-content",
        content: "edit content",
        selector: "node[type = \"atom\"]",
        onClickFunction: function (event) {
          var target = event.target || event.cyTarget;
          edit_atom = target;
           document.getElementById("editContentTrigger").click();
           this.setState({modalValue:target.data().content}, () =>
            console.log("modal value set", this.state.modalValue));
          //    $("#editContentModal").modal("show");
            //  $("#edit_atom_content").val(target.data().content);
           console.log(edit_atom, "EDIT ATOM");
        }.bind(this),
        hasTrailingDivider: false
    },
    {
        id: "edit-metadata",
        content: "edit metadata",
        selector: "node[type = \"atom\"]",
        onClickFunction: function (event) {
              //  $("#edit_metadata").empty();
              var target = event.target || event.cyTarget;
              var atom = get_atom(target.id());
              //  var textArea = $("<textarea id=\""+target.id()+"_metadata\" class=\"form-control\" rows=\"2\" >"+JSON.stringify(atom.metadata)+"</textarea>");
             //   $("#edit_metadata").append(textArea);
             //   $("#editMetadataModal").modal("show");
             edit_atom = target;
         },
         hasTrailingDivider: true
     },
     {
        id: "change-scheme",
        content: "change scheme",
        selector: "node[typeshape = \"diamond\"]",
        onClickFunction: function (event) {
            var target = event.target || event.cyTarget;
//                $("#editSchemeModal").modal("show");
edit_atom = target;
},
hasTrailingDivider: true
},
{
    id: "remove",
    content: "remove",
    selector: "node, edge",
    onClickFunction: function (event) {
        var target = event.target || event.cyTarget;
        if (selected.length !== 0) {
            selected.forEach(function(node) {
                this.delete_nodes(node);
            });
            selected = [];
        } else {
            if (target.data().type=="atom") {
                this.delete_nodes(event);
                target.remove();
            } else if (target.data().typeshape=="diamond"){
                this.delete_nodes(event);
                target.remove();
            } else {
                this.delete_edge(target.id());
                this.update_local_storage();
                target.remove();
            }
        }
    },
    hasTrailingDivider: true
},
{
    id: "add-atom",
    content: "add atom",
    coreAsWell: true,

    onClickFunction: function (event) {
        position = event.renderedPosition;
        document.getElementById("newAtomTrigger").click();
    }
},
{
    id: "add-scheme",
    content: "add scheme",
    coreAsWell: true,
    onClickFunction: function (event) {

        position = event.position || event.cyPosition;
        document.getElementById("new_scheme").click()


     //   document.getElementById("sel1").options.selectedIndex=0;
//      $("#newSchemeModal").modal("show");
},
hasTrailingDivider: true
},
{
    id: "redraw",
    content: "redraw",
    coreAsWell: true,
    onClickFunction: function () { this.redraw_visualisation(); }.bind(this),
    hasTrailingDivider: true
},
{
    id: "undo",
    content: "undo",
    selector: "node, edge",
    show: false,
    coreAsWell: true,
    onClickFunction: function (event) {
      this.undo();
  }.bind(this),
  hasTrailingDivider: false
},
{
    id: "redo",
    content: "redo",
    selector: "node, edge",
    show: false,
    coreAsWell: true,
    onClickFunction: function (event) {
      this.redo();
      if (redo_stack == []) {
        cm.hideMenuItem("redo");
    }
},
hasTrailingDivider: true
},
{
  id: "merge_nodes",
  content: "merge nodes",
  selector: "node",
  show: false,
  coreAsWell: true,
  onClickFunction: function (event) {
      this.merge_nodes();
  }
}
]
});
      cy.on("layoutstop", function(){
        running = false;

        var png = cy.png({ full: true });
        this.props.handleSetPng(png);

        var jpg = cy.jpg({ full: true });
        this.props.handleSetJpg(jpg);
    }.bind(this));

 }

 build_cola_layout( opts ) {
    var cola_params = {
        name: "cola",
        animate: true,
        randomize: true,
        padding: 100,
        fit: false,
        maxSimulationTime: 1500
    };
    var i = 0;
    if (opts !== undefined) {
        opts.forEach(function(opt) {
         cola_params[i] = opts[i];
         ++i;
     });
    }
    return cy.makeLayout( cola_params );
}

edit_atom_content() {
    var content = document.getElementById("edit_atom_content").value;
    var atom = cy.$("#"+edit_atom.id());
    update_atom_text(edit_atom.id(), content);
    this.update_local_storage();
    atom.data("content", content);
    edit_atom = null;
}

delete_nodes(event) {
    var target = event.target || event.cyTarget;
    var id = target.id();
    //removed = target.remove();
    this.delete_atom(id);
    var i = 0;
    var sds = get_sd();
    var edges = sds.edges;
    edges.forEach(function(edge) {
        if (edges[i] !== undefined) {
            if (edges[i].source_id === id || edges[i].target_id === id) {
                delete_edge(edges[i].id);
            }
            ++i;
        }
    });
    this.update_local_storage();
}

update_local_storage() {
    console.log(current_sadface_doc);
    var undo_item = JSON.parse(current_sadface_doc);
    console.log(undo_item, 'undoitem');
    undo_stack.push(undo_item);
    redo_stack = [];
    cm.showMenuItem("undo");
    cm.hideMenuItem("redo");
    localStorage.setItem("state", JSON.stringify(get_sd()));
    current_sadface_doc = JSON.stringify(get_sd());
    update();
}

redraw_visualisation() {
    layout.stop();
    layout.options.eles = cy.elements();
    layout.run();
    cy.center();
    cy.resize();
}

undo() {
    if (undo_stack.length != 0) {
        var redo_item = get_sd();
        console.log(redo_item, 'redo item');
        redo_stack.push(redo_item);
        console.log(redo_stack, 'redo stack');
        var state = undo_stack.pop();
        console.log(state, 'state');
        this.loadJSON(JSON.stringify(state));
        if (undo_stack.length == 0) {
            cm.hideMenuItem("undo");
        }
        cm.showMenuItem("redo");
    }
}

redo() {
    if (redo_stack.length != 0) {
        var undo_item = get_sd();
        undo_stack.push(undo_item);
        var state = redo_stack.pop();
        this.loadJSON(JSON.stringify(state));
        if (redo_stack.length == 0) {
            cm.hideMenuItem("redo");
        }
        cm.showMenuItem("undo");
    }
}


merge_nodes() {
    //From all currently selected nodes, set first in selected to the base node, move all sources and edges related to each other node in selected to base node
    var target;
    var id;
    var atom;
    var baseNode = selected[0].target || selected[0].cyTarget;
    var baseId = baseNode.id();
    var baseAtom = get_atom(baseId);
    var edge;
    if (baseAtom.type == "atom") {
        var sds = get_sd();
        var i = 0;
        selected.forEach(function(node){
            var j = 0;
            if (i > 0) {
                target = selected[i].target || selected[i].cyTarget;
                id = target.id();
                atom = get_atom(id);
                if (atom.type == "atom") {
                    atom.sources.forEach(function(source) {
                        add_source(baseId, atom.sources[j].resource_id, atom.sources[j].text, atom.sources[j].offset, atom.sources[j].length);
                        ++j;
                    });
                    j = 0;
                    sds.edges.forEach(function(edge) {
                        if (sds.edges[j].source_id == id) {
                            edge = add_edge(baseId,sds.edges[j].target_id);
                            cy.add([
                              { group: "edges", data: { id: edge.id.toString(), source: baseId, target: sds.edges[j].target_id } }
                              ]);
                        }
                        if (sds.edges[j].target_id == id) {
                            edge = add_edge(sds.edges[j].source_id, baseId);
                            cy.add([
                              { group: "edges", data: { id: edge.id.toString(), source: sds.edges[j].source_id, target: baseId } }
                              ]);
                        }
                        ++j;
                    });
                    this.delete_nodes(selected[i]);
                } else {
                    alert("cannot merge scheme nodes");
                }
            }
            ++i;
        });
        selected = [];
        cm.hideMenuItem("merge_nodes");
    } else {
        alert("cannot merge scheme nodes");
    }
}

delete_atom(atom_id) {
    /*
    Remove the atom from the sadface document identified by the
    supplied atom ID
    */
    if (atom_id) {
        var atom = get_atom(atom_id);
        if (atom !== null && atom !== undefined) {
            var size = Object.keys(sd.nodes).length;
            for (var i = 0; i < size; ++i) {
                if (sd.nodes[i].id === atom.id) {
                    delete sd.nodes[i];
//                    sd.nodes = remove_falsy(sd.nodes);
                        return;
                }
            }
        }
    }
}







componentDidMount(){
 this.initialise_monkeypuzzle();
}
componentWillReceiveProps(nextProps) {
   this.setState({ cy_data: nextProps.cy_data })
}

componentDidUpdate(prevProps) {
  // Typical usage (don't forget to compare props):
  if (this.props.cy_data !== prevProps.cy_data) {
  	this.initCytoscape();
  }
}



add_new_atom_node() {
    let content = this.state.modalValue;
    var meta = {"hello":"world"};
    var new_atom = add_atom(content);
    console.log(new_atom, 'new atom');
    console.log(this.state.modalValue, 'modal value');
    var atom_id = new_atom.id;
    if (focused != null) {
        add_source(atom_id, focused.id, content, 0, 0);
    }
    if (position == null) {
        position = {"x": cy.width()/2, "y": cy.height()/2};
    }
    cy.add([
        {group: "nodes", data: {id: atom_id.toString(),
            content: content, type: "atom", typeshape: "roundrectangle", metadata: meta }, 
            classes: "atom-label", locked: false, renderedPosition: position}
    ]);
    let node = cy.getElementById(atom_id.toString() );
  /*  node.qtip({
        content: function(){return 'ID: '+this.id()},
        position: {
            my: "top center",
            at: "bottom center"
        },
        style: {
            classes: "qtip-bootstrap",
            tip: {
                width: 16,
                height: 8
            }
        }
    });*/
    position = null;
    this.update_local_storage();
    this.setState({modalValue: ' '}, () =>
        console.log('modalValue reset', this.state.modalValue));
}

handleAddScheme = (e) => {
    let scheme = this.newSchemeRef.current.value;
    console.log(scheme, 'new_scheme');
    this.add_new_scheme_node(scheme)
}

 add_new_scheme_node(scheme) {
   // var scheme_idx = document.getElementById("sel1").options.selectedIndex;
  //  var scheme = document.getElementById("sel1").options[scheme_idx].text;
    var new_scheme = add_scheme(scheme);
    var scheme_id = new_scheme.id;

    cy.add([
        {group: "nodes", data: {id: scheme_id.toString(),
            content: scheme, type: "scheme", typeshape: "diamond" }, classes: "scheme-label", locked: false, position: position}
    ]);
    this.update_local_storage();
}

handleEditContent = (e) => {
    let editContent = this.editContentRef.current.value;
    let atom = cy.$("#"+edit_atom.id());
    this.update_local_storage();
    atom.data("content", editContent);
    edit_atom = null;
    this.setState({modalValue: ' '}, () =>
        console.log('modalValue reset', this.state.modalValue));
}

handleModalValueChange = (e) => {
    this.setState({modalValue: e.target.value})
}




render(){
     /* New Atom Modal */
        const newAtomModal = {
          trigger: {
            triggerText: 'New Atom',
            triggerId: 'newAtomTrigger'
          },
          title: 'New Atom',
          id: 'newAtomModal',
          content: <div className="modal-body">
          <p>Add some content to the new atom</p>
          <div className="form-group">
          <textarea 
            className="form-control" 
            rows="2" 
