update_resource(resource_id, content, title) {
    /*
    Given an ID for an existing resource, update the content and metadata of the resource.
    Updates the resource in sd;
    */


    if (resource_id && (content || title)) {
        console.log("update resource");

        var resource;
        const index = this.state.tabs.findIndex((tab) => tab.tab_id === resource_id);
        const tabs = [...this.state.tabs]
        resource = tabs[index];

        if (resource !== null && resource !== undefined) {
            if (content !== null && content !== undefined) {
                resource.content = content;
            }
            if (title !== null && title !== undefined) {
                resource.title = title;
            }
          /*  this.state.sd.resources.forEach((res) => {
                if(res.id === resource_id) {
