import React, {Component} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlusSquare } from '@fortawesome/free-solid-svg-icons'
import Modal from './Modal';
import Button from './MyButton';
import ResourcePaneTabContent from './resourcePaneTabContent'



class ResourcePane extends Component {
  constructor(props){
    super(props);

    this.state ={
      current_tab: 0,
      last_number: 1,
      tabs: [{tab_id:1},
              {tab_id:2},
                {tab_id:3},
                {tab_id:4}],
    };


   this.onDismiss = this.onDismiss.bind(this);

   this.set_active_tab = this.set_active_tab.bind(this);


  }

onDismiss(){
  console.log("hello");
}

set_active_tab(tab_id){
  if(this.state.tabs.length > 0){
      this.state.current_tab = tab_id.substring( 0, tab_id.indexOf("_body") );
      var i, tabcontent, tablinks;
      tabcontent = document.getElementsByClassName("resource_pane_tab_content");
        for (i = 0; i < tabcontent.length; i++) {
            tabcontent[i].style.display = "none";
       }
      tablinks = document.getElementsByClassName("tablinks");
        for (i = 0; i < tablinks.length; i++) {
           tablinks[i].className = tablinks[i].className.replace("active", "");
      }
    if (document.getElementById(tab_id)) {
        document.getElementById(tab_id).style.display = "block";
        document.getElementById(this.state.current_tab+"_btn").className += " active";
    }

  }
}

      /* New Resource Pane Type Selection Modal */

  newResourceModal = {
    trigger: {
      triggerText: <FontAwesomeIcon icon={faPlusSquare} size="lg" color="#cbc5c1"/>,
      className: 'add_tab_button',
    },

    title: 'New Resource Pane',
    content:  <div className="modal-body">
                <p>Select a resource type</p>
                <select className="form-control" id="resource_type">
                    <option>Text</option>
                </select>
              </div>,
    footer: {
      className: 'btn btn-primary',
      value: 'Add',
     // action: this.add_tab() 
    }

  };



  render(){


    return (
      <div className="resource-pane" id="resource-pane">
          <div className="resource_pane_tab_head" id="resource_pane_tab_head">

              <Modal
                  modalProps={this.newResourceModal}
                 /> 
              <div>
              {this.state.tabs.map(item =>{
                const onHandleSetActiveTab = () =>
                this.set_active_tab(item.tab_id+'_body');

                  return (
                    <button
                      key={item.tab_id}
                      id={item.tab_id+'_btn'}
                      onClick = {onHandleSetActiveTab}
                      type="button"
                      className="tablinks"
                    >
                      {item.tab_id}
                    </button>
                  );
                }                                                     
                  )}

              </div>
          </div>
          <div className="tab_body" id="tab_body">
            {this.state.tabs.map(item =>
                <TabBody 
                tab_id={item.tab_id}
          />
              )}


          </div>   



            </div>

      );
  }
}




class TabBody extends Component {
  render(){


    return (

        <div id={this.props.tab_id + "_body"} className="resource_pane_tab_content">
            <form>
                <div className="form-group">
                        <button type="button" className="btn btn-default" onclick="remove_tab()" title="Remove this tab from the resource pane">
                            <i className="fa fa-trash fa-fw fa-lg"></i>
                        </button>
                        <input type="file" id="load` + tab_id + `" style={{display:"none"}} accept=".txt" onchange="filemanager('load','txt','` + tab_id + `')"/>
                        <button type="button" id="load` + tab_id + `_btn" className="btn btn-default" onclick="$('#load` + tab_id + `').trigger('click');" title="Load a text file into this resource tab">
                            <i className="fa fa-upload fa-fw fa-lg"></i>
                        </button>
                        <button type="button" className="btn btn-default" onclick="filemanager('save','txt','` + tab_id + `')" title="Save this resource tab to a text file">
                            <i className="fa fa-download fa-fw fa-lg"></i>
                        </button>
                        <button id="toggle_edit_lock_button" type="button" className="btn btn-default" title="Toggle editability of the content area" onclick="toggle_edit_lock();">
                            <i id="toggle_edit_lock_icon_` + tab_id + `" className="fa fa-lock fa-fw fa-lg"></i>
                        </button>
                        <button type="button" className="btn btn-default" title="Add node from text selection" onclick="new_atom_txt_resource_button();">
                            <i className="fa fa-puzzle-piece fa-fw fa-lg"></i>
                        </button>
                </div>
                <div class="form-group">
                    <label>Title</label>
                    <textarea id="title_` + tab_id + `" type="text" rows="1" style={{resize: "none"}} className="form-control" placeholder="The name of this resource..." onchange="change_title('` + tab_id + `')"></textarea> 
                    <label>Content</label>
                    <div id="textarea">
                        <textarea id="` + tab_id + `" className="form-control" placeholder="Enter your source text here..." rows="20" onchange="change_textarea('` + tab_id + `')" onfocus="set_focus(this)" style={{resize: "vertical", minHeight: "35px"}} readonly></textarea>
                    </div>  
                </div> 
            </form>
        </div>

      );
  }
}




export default ResourcePane;
