import React, {Component} from 'react';
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
    };

    this.toggleResourcePane = this.toggleResourcePane.bind(this);
  }

   toggleResourcePane(){
      console.log('hello');
  }


  render() {
    return (

           <nav id="menu" className="menu">
          <a href="http://arg.napier.ac.uk/monkeypuzzle/">
                <header className="menu-header">
                  <span className="menu-header-title">MonkeyPuzzle</span>
                </header>
            </a>

            <section className="menu-section">
                <h3 className="menu-section-title">Interface</h3>
                <ul className="menu-section-list">            
                   <li> 
                    <Button 
                      onClick={() => this.toggleResourcePane()}
                      className="btn btn-default"
                      title="Toggle the visibility of the resources pane"
                    >
                      Toggle Resource Pane
                    </Button>
                   </li>
                    </ul>
              </section>

              </nav>
    );
  }
}

class Button extends Component {
  render(){
    const {
      onClick,
      className = '',
      children = '',
      title
    } = this.props;

    return (
       <button
        onClick={onClick}
        className={className}
        title={title}
        type="button"
      >
        {children}
      </button>
    );
  }
}



export default App;
