import './CSS/App.css';


//import fuctions from SADFACE

import {update_resource,remove_resources_from_nodes, loadSADFace,saveSADFace, delete_resource, get_sd,import_json, add_resource, export_cytoscape,add_resource_metadata} from './lib/Sadface'; 





class App extends Component{
	constructor(props){
		super(props);
		this.state = {
			tabs: [],
			last_number: 0,
			tab_id:"",
            sd: get_sd(),
            showResourcePane: true,
            current_tab: 0,
            cy_data: null,
            selected: '',
            jpg:'',
            png: '',
            fileName:'',
            json: '',
            current_sadface_doc:'',
            cy:null,
            remove_all_tabs: false,
            resourceType: 'text',
            update_local_storage: false,
            selectedText: null,
            focus: true,
            newAtomFromText: null,
        };


        this.toggleButton = this.toggleButton.bind(this);
        this.download = this.download.bind(this);
        this.loadJSON = this.loadJSON.bind(this);
        this.loadTabs =this.loadTabs.bind(this);
        this.download = this.download.bind(this);
        this.toggleResourcePane =this.toggleResourcePane.bind(this);
        this.setActiveTab = this.setActiveTab.bind(this);
        this.download = this.download.bind(this);
        this.fileManager = this.fileManager.bind(this);
        this.setCyData = this.setCyData.bind(this);

        //ResourcePane Functions
        this.add_tab = this.add_tab.bind(this);
        this.remove_tab =this.remove_tab.bind(this);
        this.change_title = this.change_title.bind(this);
        this.change_content =this.change_content.bind(this);
        this.set_text_resource_content = this.set_text_resource_content.bind(this);
        this.add_tab_to_tab_array = this.add_tab_to_tab_array.bind(this);
        this.setType = this.setType.bind(this);
        this.load_tab = this.load_tab.bind(this);
        this.updateTab = this.updateTab.bind(this);
        this.getTab = this.getTab.bind(this);
        this.setSelectedText = this.setSelectedText.bind(this);
        this.setFocus = this.setFocus.bind(this);
        this.addAtomFromText = this.addAtomFromText.bind(this);
        //Menu
        this.handleSetPng = this.handleSetPng.bind(this);
        this.handleSetJpg = this.handleSetJpg.bind(this);
        this.load_demo_argument = this.load_demo_argument.bind(this);
        this.setFileName = this.setFileName.bind(this);
        //Cytoscape
        this.clearSelection = this.clearSelection.bind(this);

    }



    add_tab(){
        let newTab = {}
        console.log('load tab null')
        newTab = add_resource(' ');
        console.log(newTab, 'newTab');
        add_resource_metadata(newTab.id, 'title', '');

        newTab.metadata = {title: ''}
        console.log(newTab, 'newTab with metadata')


        localStorage.setItem("state", JSON.stringify(get_sd()));
        newTab.type = this.state.resourceType;
        this.add_tab_to_tab_array(newTab);   
    }

    add_tab_to_tab_array(newTab){
        this.setState(state => {
            const tabs = this.state.tabs.concat(newTab);
                return {
                    tabs,
                }
            }, () => {
                    console.log(this.state.tabs, 'added to array');
                   this.setActiveTab(newTab.id);
                }
        );
    }

setType(type){
    this.setState({resourceType: type}, () =>
        console.log(this.state.resourceType, 'type set'));
}

addAtomFromText(){
    console.log(this.state.selectedText, "add atom from text");
    this.setState({newAtomFromText: this.state.selectedText}, () =>
        console.log(this.state.newAtomFromText, "addAtomFromText"));
}



    setActiveTab(value){
        this.setState({
            current_tab: value
        }, function (){
            console.log(this.state.current_tab, 'active');
        });



    }

    add_resource_metadata(resource_id, key, value) {
    /*
    Add metadata, a key:value pair to the resource dict identified
    by the supplied atom ID.    
    */
    if (resource_id && key && value) {
        this.sate.sd.resources.forEach(function(res) {
            if(res.id === resource_id) {
                res.metadata[key] = value;
            }
        });
    }
}

//Menu function
handleSetPng(png){
    this.setState({png: png}, () =>
        console.log('PNG set'));
}

handleSetJpg(jpg){
    this.setState({jpg: jpg}, () =>
        console.log('JPG set'));
}

componentDidMount() {
 this.slideout = new Slideout({
    'panel': document.getElementById('panel'),
    'menu': document.getElementById('menu'),
    "fx": "ease",
    "side": "right",
    "duration": 500,
    "padding": 256,
    "tolerance": 70,

});
}

remove_tab(i){
    delete_resource(this.state.current_tab);
    remove_resources_from_nodes(this.state.current_tab);

    localStorage.setItem("state",JSON.stringify(get_sd()));

    if (this.state.tabs.length >= 1 && i != -1){

        this.setState(state =>{
        const tabs = state.tabs.filter((item, j) => i !==j);
        return {
            tabs,
        };
    }, () => {    
        console.log(this.state.tabs.length, 'current tabs');
        let newCurrent = this.state.tabs[this.state.tabs.length-1].id
        console.log(newCurrent, 'newCurrent');
        console.log(this.state.tabs, 'tabs');
        this.setState({current_tab: newCurrent}, () =>
            console.log(this.state.current_tab, 'current tabs')
            ); 

    });


    }

};

remove_all_tabs(){
    this.setState({tabs: [], last_number:1}, () =>{
        console.log(this.state.tabs,'remove all tabs');
        });
}




toggleButton(){
    this.slideout.toggle();
}





fileManager(operation, filetype, tab_id) {
    /*
    Write text from tab to file or does a SADFace save operation
    */

