import React, {Component} from 'react';
import './App.css';

class App extends Component {
  render() {
    return (

   <nav id="menu" className="menu">
    <a href="http://arg.napier.ac.uk/monkeypuzzle/" target="_blank">
        <header className="menu-header">
          <span className="menu-header-title">MonkeyPuzzle</span>
        </header>
    </a>

    <section className="menu-section">
        <h3 className="menu-section-title">Interface</h3>
        <ul className="menu-section-list">            
           <li> <a href="#" id="toggle_resources"><button className="btn btn-default" onclick="toggle_resource_pane()" title="Toggle the visibility of the resources pane">Toggle Resource Pane</button></a></li>
            <li><a href="#" id="clear_storage_button"><button className="btn btn-danger" onclick="$('#confirm_reset_modal').modal('show');" title="Reset MonkeyPuzzle to her initial state">Reset</button></a></li>
            <li><a href="#" id="load_demo_argument_button"><button className="btn btn-default" onclick="load_demo_argument()" title="Load a demonstration SADFace document into MonkeyPuzzle">Load Demo</button></a></li>
            <li><div style="margin-left: 20px; margin-right: 60px;"><textarea id="analyst_name_textarea" type="text" rows="1" cols="20" maxlength="40" style="resize: none;" className="form-control"  placeholder="Name" title="Type your name here..." onchange="set_analyst_name();"></textarea></div></li>
            <li><div style="margin-left: 20px; margin-right: 60px;"><textarea id="analyst_email_textarea" type="text" rows="1" cols="20" maxlength="40" style="resize: none;" className="form-control"  placeholder="Email" title="Type your email address here..." onchange="set_analyst_email();"></textarea></div></li>
      </ul>
      </section>

      </nav>
    );
  }
}





const Button = ({onClick,
        className = '',
        children
        }) =>

      <button
        onClick={onClick}
        className={className}
        type="button"
      >
        {children}
      </button>



export default App;
