import React, {Component} from 'react';
import cytoscape from 'cytoscape';
import cola from 'cytoscape-cola';
import Tooltip from 'react-qtip';



cytoscape.use( cola );


class CytoContainer extends Component{
    constructor(props){
        super(props);
        this.state= {
            //cy_data: this.props.cy_data,
        };

    }

    renderCytoscape(){
//        this.cy_data = {};
        this.cy = cytoscape(
        {
            container: document.getElementById('cy'),
            ready: function(){ window.cy = this; },
          //  elements: this.props.cy_data,
           elements: {
                nodes: [
                    { data: { id: 'cat' } },
                    { data: { id: 'bird' } },
                    { data: { id: 'ladybug' } },
                    { data: { id: 'aphid' } },
                    { data: { id: 'rose' } },
                    { data: { id: 'grasshopper' } },
                    { data: { id: 'plant' } },
                    { data: { id: 'wheat' } }
                ],
                edges: [
                    { data: { source: 'cat', target: 'bird' } },
                    { data: { source: 'bird', target: 'ladybug' } },
                    { data: { source: 'bird', target: 'grasshopper' } },
                    { data: { source: 'grasshopper', target: 'plant' } },
                    { data: { source: 'grasshopper', target: 'wheat' } },
                    { data: { source: 'ladybug', target: 'aphid' } },
                    { data: { source: 'aphid', target: 'rose' } }
                ]
                },

                style:[
                {   selector: "node", 
                    style: {
                        "content": "data(content)",
                        "text-opacity": 0.7,
                        "width" : "auto",
                        "height" : "auto",
                        "text-valign": "bottom",
                        "text-halign": "right",
                        "text-outline-color": "#eee",
                        "text-outline-width": 1
                    }
                },
                {   selector: "[typeshape]", 
                    style: {
                        "shape":"data(typeshape)"
                    }   
                },
                {   selector: "node[typeshape='diamond']", 
                    style: {
                        'background-color': '#CC9900'
                }
                },
                {
                selector: "node[typeshape='diamond'][content='Conflict']",
                style: {
                    'background-color': 'red'
                }
                },
                {
                selector: "node[typeshape='diamond'][content='Support']",
                style: {
                    'background-color': 'green'
                }
                },
                {   
                selector: "edge", 
                style: {
                    "line-color": "#9dbaea",
                    "target-arrow-shape": "triangle",
                    "target-arrow-color": "#9dbaea",
                    "curve-style": "bezier"
                }
                },
                {   
                selector: ":selected", 
                style: {
                    "border-width":"1",
                    "border-color":"black",
                    "background-color": "#3399CC"
                }
                },
                {   
                selector: ".atom-label", 
                style:{
                    "text-wrap": "wrap",
                    "text-max-width": 160
                }
                },
                {   
                selector: ".scheme-label", 
                style:{
                    "text-wrap": "wrap",
                    "text-max-width": 160
                }
            }
            ],

            boxSelectionEnabled: false,
            autounselectify: false,
            selectionType: "single",
            minZoom: 0.1,
            maxZoom: 1.5

        });

        this.layout = this.build_cola_layout();
        this.layout.run();

        this.cy.elements(<Tooltip text="simple text">Hello</Tooltip>)


    }//close rendercytoscape

    build_cola_layout( opts ) {
    var cola_params = {
        name: "cola",
        animate: true,
        randomize: true,
        padding: 100,
        fit: false,
        maxSimulationTime: 1500
    };
    var i = 0;
    if (opts !== undefined) {
        opts.forEach(function(opt) {
           cola_params[i] = opts[i];
           ++i;
        });
    }
    return this.cy.makeLayout( cola_params );
}

    componentDidMount(){
        this.renderCytoscape();
    }


    render(){
        return(
            <div id="cy"></div>
            );
    }
}

export default CytoContainer;
